import numpy as np
import heapq

def hybrid_enhanced_commitment(units_info, load):
    load_curr = load[0]
    units = [unit.copy() for unit in units_info]

    # 1. Classify units
    must_on = []
    must_off = []
    free_on = []
    free_off = []

    for unit in units:
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_on.append(unit)
            else:
                free_on.append(unit)
        else:
            if -unit['t_i_0'] < unit['t_off_min_i']:
                must_off.append(unit)
            else:
                free_off.append(unit)

    # 2. Compute effective min/max
    for unit in must_on + free_on:
        unit['eff_min'] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        unit['eff_max'] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])

    for unit in free_off:
        unit['eff_min'] = unit['p_min_i']
        unit['eff_max'] = min(unit['p_max_i'], unit['p_start_i'])

    # 3. Check must-on alone
    muston_min = sum(unit['eff_min'] for unit in must_on)
    muston_max = sum(unit['eff_max'] for unit in must_on)

    if muston_min > load_curr:
        for unit in must_on:
            unit['u_i'] = 1
            unit['p_i'] = unit['eff_min']
        for unit in free_on + free_off + must_off:
            unit['u_i'] = 0
            unit['p_i'] = 0.0
        return build_output(units)

    elif muston_min <= load_curr <= muston_max:
        committed = must_on
        for unit in must_on:
            unit['u_i'] = 1
        for unit in free_on + free_off + must_off:
            unit['u_i'] = 0
            unit['p_i'] = 0.0
        dispatch_load(committed, load_curr)
        return build_output(units)

    # 5. Expand and reduce committed set
    current_committed = must_on + free_on
    total_min = sum(unit['eff_min'] for unit in current_committed)
    total_max = sum(unit['eff_max'] for unit in current_committed)

    if total_max < load_curr:
        free_off_sorted = sorted(
            free_off,
            key=lambda u: (u['s_i'] + u['a_i'] + u['b_i']*u['eff_min'] + u['c_i']*u['eff_min']**2) / u['eff_min']
        )
        for unit in free_off_sorted:
            current_committed.append(unit)
            total_max += unit['eff_max']
            if total_max >= load_curr:
                break

    total_min = sum(unit['eff_min'] for unit in current_committed)
    non_must_units = [u for u in current_committed if u not in must_on]
    non_must_units_sorted = sorted(
        non_must_units,
        key=lambda u: ((u['s_i'] if u in free_off else 0) + u['a_i'] + u['b_i']*u['eff_min'] + u['c_i']*u['eff_min']**2) / u['eff_min'],
        reverse=True
    )

    for unit in non_must_units_sorted:
        new_min = total_min - unit['eff_min']
        new_max = total_max - unit['eff_max']
        if new_min <= load_curr <= new_max:
            current_committed.remove(unit)
            total_min = new_min
            total_max = new_max

    # 6. Dispatch and assign final states
    for unit in units:
        unit['u_i'] = 1 if unit in current_committed else 0
        unit['p_i'] = unit['eff_min'] if unit in current_committed else 0.0

    if total_min <= load_curr:
        dispatch_load(current_committed, load_curr)

    for unit in must_off + free_off:
        if unit not in current_committed:
            unit['u_i'] = 0
            unit['p_i'] = 0.0

    return build_output(units)

def dispatch_load(committed, load_curr):
    outputs = [unit['eff_min'] for unit in committed]
    total_assigned = sum(outputs)
    deficit = load_curr - total_assigned

    if deficit <= 1e-5:
        for i, unit in enumerate(committed):
            unit['p_i'] = outputs[i]
        return

    heap = []
    for i, unit in enumerate(committed):
        headroom = unit['eff_max'] - unit['eff_min']
        if headroom > 1e-5:
            mc = unit['b_i'] + 2 * unit['c_i'] * outputs[i]
            heapq.heappush(heap, (mc, i, outputs[i], headroom))

    while deficit > 1e-5 and heap:
        mc, idx, cur, headroom = heapq.heappop(heap)
        assign_amount = min(headroom, deficit)
        new_cur = cur + assign_amount
        outputs[idx] = new_cur
        deficit -= assign_amount
        headroom -= assign_amount

        if headroom > 1e-5:
            new_mc = committed[idx]['b_i'] + 2 * committed[idx]['c_i'] * new_cur
            heapq.heappush(heap, (new_mc, idx, new_cur, headroom))

    for i, unit in enumerate(committed):
        unit['p_i'] = outputs[i]

def build_output(units):
    u_list = [unit['u_i'] for unit in units]
    p_list = [unit['p_i'] for unit in units]
    return np.array([u_list, p_list])
