import numpy as np

def commit_units_rolling_heuristic(units_info, load):
    current_load = load[0]
    num_units = len(units_info)
    u = np.zeros(num_units)
    p = np.zeros(num_units)
    must_on = []
    must_off = []
    flexible = []

    # Classify units
    for i, unit in enumerate(units_info):
        u_i0 = unit['u_i_0']
        t_i0 = unit['t_i_0']
        p_i0 = unit['p_i_0']

        if u_i0 == 1:
            if t_i0 < unit['t_on_min_i'] or p_i0 > unit['p_shut_i']:
                must_on.append(i)
            else:
                flexible.append(i)
        else:
            if abs(t_i0) < unit['t_off_min_i']:
                must_off.append(i)
            else:
                flexible.append(i)

    # Calculate feasible ranges for must-on units
    ranges = []
    for i in must_on:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            lb = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            ub = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            lb = unit['p_min_i']
            ub = min(unit['p_max_i'], unit['p_start_i'])
        ranges.append((lb, ub))

    total_min = sum(lb for lb, ub in ranges)
    total_max = sum(ub for lb, ub in ranges)

    # Determine flexible units to activate
    flexible_activated = []
    if current_load > total_max:
        # Sort flexible units by incremental cost at min output
        costs = []
        for i in flexible:
            unit = units_info[i]
            inc_cost = unit['b_i'] + 2 * unit['c_i'] * unit['p_min_i']
            costs.append((inc_cost, i))
        costs.sort(key=lambda x: x[0])

        for _, i in costs:
            unit = units_info[i]
            if unit['u_i_0'] == 1:
                ub = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                ub = min(unit['p_max_i'], unit['p_start_i'])
            total_max += ub
            flexible_activated.append(i)
            if total_max >= current_load:
                break

    # Commit units
    committed = must_on + flexible_activated
    for i in committed:
        u[i] = 1

    # Initialize outputs to minimum feasible
    for idx, i in enumerate(committed):
        unit = units_info[i]
        if i in must_on:
            p[i] = ranges[idx][0]
        else:
            p[i] = unit['p_min_i']

    # Dispatch remaining load
    remaining = current_load - sum(p)
    if remaining > 0:
        # Sort by incremental cost
        inc_costs = []
        for i in committed:
            unit = units_info[i]
            current_p = p[i]
            inc_cost = unit['b_i'] + 2 * unit['c_i'] * current_p
            if i in must_on:
                max_add = ranges[must_on.index(i)][1] - current_p
            else:
                if unit['u_i_0'] == 1:
                    max_add = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i']) - current_p
                else:
                    max_add = min(unit['p_max_i'], unit['p_start_i']) - current_p
            inc_costs.append((inc_cost, i, max_add))

        inc_costs.sort(key=lambda x: x[0])

        for _, i, max_add in inc_costs:
            if remaining <= 0:
                break
            add = min(remaining, max_add)
            p[i] += add
            remaining -= add

    # Handle must-off units
    for i in must_off:
        u[i] = 0
        p[i] = 0

    return np.array([u, p])
