import numpy as np

def dynamic_priority_heuristic(units_info, load):
    current_load = load[0]
    n_units = len(units_info)
    forced_on = [False] * n_units
    forced_off = [False] * n_units

    # Enforce constraints
    for idx, unit in enumerate(units_info):
        u_i0 = unit['u_i_0']
        t_i0 = unit['t_i_0']
        p_i0 = unit['p_i_0']
        t_on_min = unit['t_on_min_i']
        t_off_min = unit['t_off_min_i']
        p_shut = unit['p_shut_i']

        if u_i0 == 1:
            if t_i0 < t_on_min or p_i0 > p_shut:
                forced_on[idx] = True
        else:
            if abs(t_i0) < t_off_min:
                forced_off[idx] = True

    u = [0] * n_units
    p = [0] * n_units

    # Commit forced units
    for idx in range(n_units):
        if forced_on[idx]:
            u[idx] = 1
        if forced_off[idx]:
            u[idx] = 0

    # Priority calculation
    eligible_units = []
    for idx in range(n_units):
        if forced_on[idx] or forced_off[idx]:
            continue
        unit = units_info[idx]
        p_min = unit['p_min_i']
        if unit['u_i_0'] == 1:
            priority = (unit['b_i'] + 2 * unit['c_i'] * p_min) / p_min
        else:
            amortized_startup = unit['s_i'] / (unit['t_on_min_i'] * p_min)
            priority = (unit['b_i'] + 2 * unit['c_i'] * p_min) / p_min + amortized_startup
        eligible_units.append((priority, idx))

    eligible_units.sort(key=lambda x: x[0])

    # Commit by priority
    total_max = 0
    for idx in range(n_units):
        if u[idx] == 1:
            unit = units_info[idx]
            if unit['u_i_0'] == 1:
                total_max += min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                total_max += min(unit['p_max_i'], unit['p_start_i'], unit['p_up_i'])

    for priority, idx in eligible_units:
        if total_max >= current_load:
            break
        u[idx] = 1
        unit = units_info[idx]
        if unit['u_i_0'] == 1:
            total_max += min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            total_max += min(unit['p_max_i'], unit['p_start_i'], unit['p_up_i'])

    # Initialize outputs
    total_power = 0
    for idx in range(n_units):
        if u[idx] == 1:
            unit = units_info[idx]
            if unit['u_i_0'] == 1:
                lb = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                p[idx] = lb
            else:
                p[idx] = unit['p_min_i']
            total_power += p[idx]

    # Economic dispatch
    tolerance = 0.1
    while abs(total_power - current_load) > tolerance:
        if total_power < current_load:  # Need more power
            min_mc = float('inf')
            candidate = -1
            max_inc = 0
            for idx in range(n_units):
                if u[idx] == 0:
                    continue
                unit = units_info[idx]
                mc = 2 * unit['c_i'] * p[idx] + unit['b_i']
                # Compute headroom
                if unit['u_i_0'] == 1:
                    ub = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
                else:
                    ub = min(unit['p_max_i'], unit['p_start_i'], unit['p_up_i'])
                headroom = ub - p[idx]
                if headroom > tolerance and mc < min_mc:
                    min_mc = mc
                    candidate = idx
                    max_inc = headroom
            if candidate == -1:
                break
            inc = min(max_inc, current_load - total_power)
            p[candidate] += inc
            total_power += inc

        else:  # Need less power
            max_mc = -float('inf')
            candidate = -1
            max_dec = 0
            for idx in range(n_units):
                if u[idx] == 0 or units_info[idx]['u_i_0'] == 0:
                    continue
                unit = units_info[idx]
                mc = 2 * unit['c_i'] * p[idx] + unit['b_i']
                lb = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                footroom = p[idx] - lb
                if footroom > tolerance and mc > max_mc:
                    max_mc = mc
                    candidate = idx
                    max_dec = footroom
            if candidate == -1:
                break
            dec = min(max_dec, total_power - current_load)
            p[candidate] -= dec
            total_power -= dec

    return np.array([u, p])

import numpy as np

def rolling_commitment_heuristic(units_info, load):
    current_load = load[0]
    n_units = len(units_info)
    must_on = []
    must_off = []
    free_units = []
    committed = set()

    # Classify units and initial commitment
    for i, unit in enumerate(units_info):
        u0 = unit['u_i_0']
        t0 = unit['t_i_0']
        p0 = unit['p_i_0']

        if u0 == 1:
            if t0 < unit['t_on_min_i'] or p0 > unit['p_shut_i']:
                must_on.append(i)
            else:
                free_units.append(i)
                committed.add(i)
        else:
            if t0 < 0 and -t0 < unit['t_off_min_i']:
                must_off.append(i)
            else:
                free_units.append(i)

    committed |= set(must_on)
    free_on = [i for i in free_units if units_info[i]['u_i_0'] == 1]
    free_off = [i for i in free_units if units_info[i]['u_i_0'] == 0]

    # Calculate power ranges
    def calc_power(i, com_set):
        unit = units_info[i]
        if i in com_set:
            if unit['u_i_0'] == 1:
                return (max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i']),
                        min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i']))
            else:
                return (unit['p_min_i'], min(unit['p_max_i'], unit['p_start_i']))
        return (0, 0)

    min_pow, max_pow = 0, 0
    for i in range(n_units):
        mi, ma = calc_power(i, committed)
        min_pow += mi
        max_pow += ma

    # Turn off expensive units if overcommitted
    if min_pow > current_load:
        off_candidates = [(i, *calc_power(i, committed)) for i in free_on if i not in must_on]
        off_candidates.sort(key=lambda x: (units_info[x[0]]['a_i'] + units_info[x[0]]['b_i'] * x[1] + 
                                          units_info[x[0]]['c_i'] * x[1]**2) / x[1], reverse=True)
        for i, mini, maxi in off_candidates:
            if min_pow <= current_load:
                break
            new_min = min_pow - mini
            new_max = max_pow - maxi
            if new_max >= current_load:
                committed.discard(i)
                min_pow = new_min
                max_pow = new_max

    # Turn on cheap units if undercommitted
    if max_pow < current_load:
        on_candidates = []
        for i in free_off:
            unit = units_info[i]
            min_i = unit['p_min_i']
            max_i = min(unit['p_max_i'], unit['p_start_i'])
            avg_cost = (unit['s_i'] + unit['a_i'] + unit['b_i'] * min_i + 
                        unit['c_i'] * min_i**2) / min_i
            on_candidates.append((i, min_i, max_i, avg_cost))
        on_candidates.sort(key=lambda x: x[3])
        for i, min_i, max_i, _ in on_candidates:
            if max_pow >= current_load:
                break
            committed.add(i)
            min_pow += min_i
            max_pow += max_i

    # Initial dispatch to minimum possible
    dispatch = np.zeros(n_units)
    for i in range(n_units):
        if i in committed:
            if units_info[i]['u_i_0'] == 1:
                dispatch[i] = max(units_info[i]['p_min_i'], 
                                 units_info[i]['p_i_0'] - units_info[i]['p_down_i'])
            else:
                dispatch[i] = units_info[i]['p_min_i']

    total = np.sum(dispatch)
    deficit = current_load - total

    # Ramp up units if needed
    if deficit > 0:
        ramp_candidates = []
        for i in range(n_units):
            if i in committed:
                unit = units_info[i]
                if unit['u_i_0'] == 1:
                    max_i = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
                else:
                    max_i = min(unit['p_max_i'], unit['p_start_i'])
                if dispatch[i] < max_i:
                    marginal = 2 * unit['c_i'] * dispatch[i] + unit['b_i']
                    ramp_candidates.append((i, marginal, max_i - dispatch[i]))

        ramp_candidates.sort(key=lambda x: x[1])
        for i, mc, available in ramp_candidates:
            if deficit <= 0:
                break
            alloc = min(available, deficit)
            dispatch[i] += alloc
            deficit -= alloc

    # Prepare schedule
    u_state = [1 if i in committed else 0 for i in range(n_units)]
    schedules = np.array([u_state, dispatch])
    return schedules

import numpy as np

def rolling_combined_heuristic(units_info, load):
    n_units = len(units_info)
    current_load = load[0]  
    next_load = load[1]  

    forced_on = []  
    forced_off = []  
    flex_online = []  
    flex_offline = []  
    min_bound = np.zeros(n_units)  
    max_bound = np.zeros(n_units)  
    commited_indices = set()  
    outputs = np.zeros(n_units)  
    u = np.zeros(n_units)  

    for i, unit in enumerate(units_info):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                forced_on.append(i)
            else:
                flex_online.append(i)
        else:
            if abs(unit['t_i_0']) < unit['t_off_min_i']:
                forced_off.append(i)
            else:
                flex_offline.append(i)

        if unit['u_i_0'] == 1:
            min_bound[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            max_bound[i] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            min_bound[i] = unit['p_min_i']
            max_bound[i] = min(unit['p_max_i'], unit['p_start_i'])

    commited_indices = set(forced_on) | set(flex_online)

    total_min = sum(min_bound[i] for i in commited_indices)
    total_max = sum(max_bound[i] for i in commited_indices)

    flex_online_sorted = sorted(
        flex_online,
        key=lambda i: (units_info[i]['a_i'] + units_info[i]['b_i'] * min_bound[i] + 
                        units_info[i]['c_i'] * min_bound[i]**2) / min_bound[i],
        reverse=True
    )
    for i in flex_online_sorted:
        if total_min <= current_load:
            break
        if i in commited_indices:
            commited_indices.remove(i)
            total_min -= min_bound[i]
            total_max -= max_bound[i]

    flex_offline_sorted = sorted(
        flex_offline,
        key=lambda i: (units_info[i]['s_i'] + 2 * (units_info[i]['a_i'] + 
                        units_info[i]['b_i'] * min_bound[i] + 
                        units_info[i]['c_i'] * min_bound[i]**2)) / (2 * min_bound[i])
    )
    for i in flex_offline_sorted:
        if total_max >= current_load:
            break
        commited_indices.add(i)
        total_min += min_bound[i]
        total_max += max_bound[i]

    commited_list = list(commited_indices)

    if total_min > current_load:
        outputs = np.array([min_bound[i] if i in commited_indices else 0 for i in range(n_units)])
    elif total_max < current_load:
        outputs = np.array([max_bound[i] if i in commited_indices else 0 for i in range(n_units)])
    else:
        lambda_low = min(2*units_info[i]['c_i']*min_bound[i] + units_info[i]['b_i'] for i in commited_list)
        lambda_high = max(2*units_info[i]['c_i']*max_bound[i] + units_info[i]['b_i'] for i in commited_list)
        tol = 0.1

        for _ in range(50):
            mid_lambda = (lambda_low + lambda_high) / 2
            total_p = 0.0
            for i in commited_list:
                unit = units_info[i]
                p_val = (mid_lambda - unit['b_i']) / (2 * unit['c_i'])
                p_clamped = np.clip(p_val, min_bound[i], max_bound[i])
                outputs[i] = p_clamped
                total_p += p_clamped

            if abs(total_p - current_load) <= tol:
                break
            elif total_p < current_load:
                lambda_low = mid_lambda
            else:
                lambda_high = mid_lambda
        else:
            outputs = np.zeros(n_units)
            for i in commited_indices:
                outputs[i] = min_bound[i]

    for i in commited_indices:
        u[i] = 1

    return np.array([u, outputs])
