import numpy as np

def refined_commit_priority_ramp_aware(units_info, load):
    current_load = load[0]
    forced_on = []
    forced_off = []
    free_online = []
    free_offline = []

    for unit in units_info:
        u_i0 = unit['u_i_0']
        t_i0 = unit['t_i_0']
        p_i0 = unit['p_i_0']

        if u_i0 == 1:
            if (t_i0 < unit['t_on_min_i']) or (p_i0 > unit['p_shut_i']):
                forced_on.append(unit)
            else:
                free_online.append(unit)
        else:
            if -t_i0 < unit['t_off_min_i']:
                forced_off.append(unit)
            else:
                free_offline.append(unit)

    for unit in forced_on + free_online:
        unit['p_min_current'] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        unit['p_max_current'] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        p_min = unit['p_min_current']
        unit['cost_rate'] = (unit['a_i'] + unit['b_i']*p_min + unit['c_i']*p_min**2) / p_min

    for unit in free_offline:
        unit['p_min_current'] = unit['p_min_i']
        unit['p_max_current'] = min(unit['p_max_i'], unit['p_start_i'])
        p_min = unit['p_min_current']
        unit['cost_rate'] = (unit['a_i'] + unit['s_i'] + unit['b_i']*p_min + unit['c_i']*p_min**2) / p_min

    candidates = sorted(free_online + free_offline, key=lambda u: u['cost_rate'])
    committed = forced_on.copy()
    total_min = sum(u['p_min_current'] for u in committed)
    total_max = sum(u['p_max_current'] for u in committed)
    skipped = []

    for unit in candidates:
        if total_max >= current_load:
            break
        if total_min + unit['p_min_current'] <= current_load:
            committed.append(unit)
            total_min += unit['p_min_current']
            total_max += unit['p_max_current']
        else:
            skipped.append(unit)

    if total_max < current_load:
        for unit in skipped:
            committed.append(unit)
            total_min += unit['p_min_current']
            total_max += unit['p_max_current']

    target = min(max(total_min, current_load), total_max)

    if committed:
        low = min(u['b_i'] + 2*u['c_i']*u['p_min_current'] for u in committed)
        high = max(u['b_i'] + 2*u['c_i']*u['p_max_current'] for u in committed)

        for _ in range(100):
            lam = (low + high) / 2
            total = 0
            for u in committed:
                if lam <= u['b_i'] + 2*u['c_i']*u['p_min_current']:
                    total += u['p_min_current']
                elif lam >= u['b_i'] + 2*u['c_i']*u['p_max_current']:
                    total += u['p_max_current']
                else:
                    total += (lam - u['b_i']) / (2 * u['c_i'])

            if abs(total - target) < 1e-5:
                break
            if total < target:
                low = lam
            else:
                high = lam

        for u in committed:
            if lam <= u['b_i'] + 2*u['c_i']*u['p_min_current']:
                u['p_i'] = u['p_min_current']
            elif lam >= u['b_i'] + 2*u['c_i']*u['p_max_current']:
                u['p_i'] = u['p_max_current']
            else:
                u['p_i'] = (lam - u['b_i']) / (2 * u['c_i'])

    schedules = [[], []]
    for unit in units_info:
        if unit in committed:
            schedules[0].append(1)
            schedules[1].append(unit.get('p_i', 0.0))
        else:
            schedules[0].append(0)
            schedules[1].append(0.0)

    return np.array(schedules)

import numpy as np

def dynamic_priority_heuristic(units_info, load):
    current_load = load[0]
    n_units = len(units_info)
    forced_on = [False] * n_units
    forced_off = [False] * n_units

    # Enforce constraints
    for idx, unit in enumerate(units_info):
        u_i0 = unit['u_i_0']
        t_i0 = unit['t_i_0']
        p_i0 = unit['p_i_0']
        t_on_min = unit['t_on_min_i']
        t_off_min = unit['t_off_min_i']
        p_shut = unit['p_shut_i']

        if u_i0 == 1:
            if t_i0 < t_on_min or p_i0 > p_shut:
                forced_on[idx] = True
        else:
            if abs(t_i0) < t_off_min:
                forced_off[idx] = True

    u = [0] * n_units
    p = [0] * n_units

    # Commit forced units
    for idx in range(n_units):
        if forced_on[idx]:
            u[idx] = 1
        if forced_off[idx]:
            u[idx] = 0

    # Priority calculation
    eligible_units = []
    for idx in range(n_units):
        if forced_on[idx] or forced_off[idx]:
            continue
        unit = units_info[idx]
        p_min = unit['p_min_i']
        if unit['u_i_0'] == 1:
            priority = (unit['b_i'] + 2 * unit['c_i'] * p_min) / p_min
        else:
            amortized_startup = unit['s_i'] / (unit['t_on_min_i'] * p_min)
            priority = (unit['b_i'] + 2 * unit['c_i'] * p_min) / p_min + amortized_startup
        eligible_units.append((priority, idx))

    eligible_units.sort(key=lambda x: x[0])

    # Commit by priority
    total_max = 0
    for idx in range(n_units):
        if u[idx] == 1:
            unit = units_info[idx]
            if unit['u_i_0'] == 1:
                total_max += min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                total_max += min(unit['p_max_i'], unit['p_start_i'], unit['p_up_i'])

    for priority, idx in eligible_units:
        if total_max >= current_load:
            break
        u[idx] = 1
        unit = units_info[idx]
        if unit['u_i_0'] == 1:
            total_max += min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            total_max += min(unit['p_max_i'], unit['p_start_i'], unit['p_up_i'])

    # Initialize outputs
    total_power = 0
    for idx in range(n_units):
        if u[idx] == 1:
            unit = units_info[idx]
            if unit['u_i_0'] == 1:
                lb = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                p[idx] = lb
            else:
                p[idx] = unit['p_min_i']
            total_power += p[idx]

    # Economic dispatch
    tolerance = 0.1
    while abs(total_power - current_load) > tolerance:
        if total_power < current_load:  # Need more power
            min_mc = float('inf')
            candidate = -1
            max_inc = 0
            for idx in range(n_units):
                if u[idx] == 0:
                    continue
                unit = units_info[idx]
                mc = 2 * unit['c_i'] * p[idx] + unit['b_i']
                # Compute headroom
                if unit['u_i_0'] == 1:
                    ub = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
                else:
                    ub = min(unit['p_max_i'], unit['p_start_i'], unit['p_up_i'])
                headroom = ub - p[idx]
                if headroom > tolerance and mc < min_mc:
                    min_mc = mc
                    candidate = idx
                    max_inc = headroom
            if candidate == -1:
                break
            inc = min(max_inc, current_load - total_power)
            p[candidate] += inc
            total_power += inc

        else:  # Need less power
            max_mc = -float('inf')
            candidate = -1
            max_dec = 0
            for idx in range(n_units):
                if u[idx] == 0 or units_info[idx]['u_i_0'] == 0:
                    continue
                unit = units_info[idx]
                mc = 2 * unit['c_i'] * p[idx] + unit['b_i']
                lb = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                footroom = p[idx] - lb
                if footroom > tolerance and mc > max_mc:
                    max_mc = mc
                    candidate = idx
                    max_dec = footroom
            if candidate == -1:
                break
            dec = min(max_dec, total_power - current_load)
            p[candidate] -= dec
            total_power -= dec

    return np.array([u, p])
