def refined_avg_cost_commitment(units_info, load):
    n = len(units_info)
    u = np.zeros(n)
    p = np.zeros(n)

    a = [unit['a_i'] for unit in units_info]
    b = [unit['b_i'] for unit in units_info]
    c = [unit['c_i'] for unit in units_info]
    p_min = [unit['p_min_i'] for unit in units_info]
    p_max = [unit['p_max_i'] for unit in units_info]
    p_up = [unit['p_up_i'] for unit in units_info]
    p_down = [unit['p_down_i'] for unit in units_info]
    p_start = [unit['p_start_i'] for unit in units_info]
    p_shut = [unit['p_shut_i'] for unit in units_info]
    t_on_min = [unit['t_on_min_i'] for unit in units_info]
    t_off_min = [unit['t_off_min_i'] for unit in units_info]
    s = [unit['s_i'] for unit in units_info]
    u_0 = [unit['u_i_0'] for unit in units_info]
    t_0 = [unit['t_i_0'] for unit in units_info]
    p_0 = [unit['p_i_0'] for unit in units_info]

    must_run = []
    must_off = []
    flexible = []

    for i in range(n):
        if u_0[i] == 1:
            if t_0[i] < t_on_min[i] or p_0[i] > p_shut[i]:
                must_run.append(i)
            else:
                flexible.append(i)
        else:
            if t_0[i] > -t_off_min[i]:
                must_off.append(i)
            else:
                flexible.append(i)

    for i in must_run:
        u[i] = 1
        if u_0[i] == 1:
            p[i] = max(p_min[i], p_0[i] - p_down[i])
        else:
            p[i] = p_min[i]

    for i in must_off:
        u[i] = 0
        p[i] = 0

    avg_costs = []
    for i in flexible:
        if u_0[i] == 0:
            cost = (s[i] + b[i]*p_min[i] + c[i]*p_min[i]**2) / p_min[i]
        else:
            cost = (b[i]*p_min[i] + c[i]*p_min[i]**2) / p_min[i]
        avg_costs.append((cost, i))

    avg_costs.sort(key=lambda x: x[0])

    total_max_cap = sum(p_max[i] for i in must_run)
    flexible_committed = []

    for cost, i in avg_costs:
        if total_max_cap >= load:
            break
        u[i] = 1
        if u_0[i] == 1:
            p[i] = max(p_min[i], p_0[i] - p_down[i])
            unit_max = min(p_max[i], p_0[i] + p_up[i])
        else:
            p[i] = p_min[i]
            unit_max = min(p_max[i], p_start[i])
        total_max_cap += unit_max
        flexible_committed.append(i)

    for cost, i in avg_costs[len(flexible_committed):]:
        u[i] = 0
        p[i] = 0

    committed_indices = must_run + flexible_committed
    current_min = [0] * n
    current_max = [0] * n

    for i in committed_indices:
        if u_0[i] == 1:
            current_min[i] = max(p_min[i], p_0[i] - p_down[i])
            current_max[i] = min(p_max[i], p_0[i] + p_up[i])
        else:
            current_min[i] = p_min[i]
            current_max[i] = min(p_max[i], p_start[i])

    total_output = sum(p)

    while abs(total_output - load) > 1e-5:
        deficit = load - total_output
        if deficit > 0:
            best_idx = None
            best_marginal = float('inf')
            for i in committed_indices:
                if p[i] < current_max[i]:
                    marginal = b[i] + 2*c[i]*p[i]
                    if marginal < best_marginal:
                        best_marginal = marginal
                        best_idx = i
            if best_idx is None:
                break
            amount = min(1, deficit, current_max[best_idx] - p[best_idx])
            p[best_idx] += amount
            total_output += amount
        else:
            best_idx = None
            best_marginal = -float('inf')
            for i in committed_indices:
                if p[i] > current_min[i]:
                    marginal = b[i] + 2*c[i]*p[i]
                    if marginal > best_marginal:
                        best_marginal = marginal
                        best_idx = i
            if best_idx is None:
                break
            amount = min(1, -deficit, p[best_idx] - current_min[best_idx])
            p[best_idx] -= amount
            total_output -= amount

    return np.array([u, p])

