def hybrid_adaptive_cost_commitment(units_info, load):
    n_units = len(units_info)
    u = np.zeros(n_units)
    p = np.zeros(n_units)

    must_run = []
    must_off = []
    flexible = []

    for i, unit in enumerate(units_info):
        u_i0 = unit['u_i_0']
        t_i0 = unit['t_i_0']
        p_i0 = unit['p_i_0']

        if u_i0 == 1:
            if t_i0 < unit['t_on_min_i'] or p_i0 > unit['p_shut_i']:
                must_run.append(i)
            else:
                flexible.append(i)
        else:
            if t_i0 > -unit['t_off_min_i']:
                must_off.append(i)
            else:
                flexible.append(i)

    total_min_output = 0
    total_max_capacity = 0

    for i in must_run:
        unit = units_info[i]
        u[i] = 1
        p_min = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        p[i] = p_min
        total_min_output += p_min
        total_max_capacity += min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])

    for i in must_off:
        u[i] = 0
        p[i] = 0

    def calculate_adaptive_cost(unit, is_offline):
        p_min = unit['p_min_i']
        if is_offline:
            avg_cost = (unit['s_i'] + unit['b_i'] * p_min + unit['c_i'] * p_min**2) / p_min
            marg_cost = unit['b_i'] + 2 * unit['c_i'] * p_min
        else:
            avg_cost = (unit['b_i'] * p_min + unit['c_i'] * p_min**2) / p_min
            marg_cost = unit['b_i'] + 2 * unit['c_i'] * p_min
        return 0.7 * avg_cost + 0.3 * marg_cost

    flexible_costs = []
    for i in flexible:
        unit = units_info[i]
        is_offline = unit['u_i_0'] == 0
        cost = calculate_adaptive_cost(unit, is_offline)
        flexible_costs.append((cost, i))

    flexible_costs.sort(key=lambda x: x[0])

    current_capacity = total_max_capacity
    committed_flexible = []

    for cost, i in flexible_costs:
        if current_capacity >= load:
            break
        unit = units_info[i]
        if unit['u_i_0'] == 0:
            max_cap = min(unit['p_max_i'], unit['p_start_i'])
        else:
            max_cap = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        current_capacity += max_cap
        u[i] = 1
        committed_flexible.append(i)

    committed_units = must_run + committed_flexible

    for i in committed_units:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            p_min = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        else:
            p_min = unit['p_min_i']
        p[i] = p_min

    total_output = np.sum(p)
    deficit = load - total_output

    while abs(deficit) > 1e-5:
        if deficit > 0:
            candidates = []
            for i in committed_units:
                unit = units_info[i]
                if unit['u_i_0'] == 0:
                    p_max = min(unit['p_max_i'], unit['p_start_i'])
                else:
                    p_max = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
                if p[i] < p_max:
                    cost = unit['b_i'] + 2 * unit['c_i'] * p[i]
                    candidates.append((cost, i, p_max - p[i]))
            if not candidates:
                break
            candidates.sort(key=lambda x: x[0])
            best_i = candidates[0][1]
            step = min(deficit, candidates[0][2], units_info[best_i]['p_up_i'] if units_info[best_i]['u_i_0'] == 1 else float('inf'))
            p[best_i] += step
            deficit -= step
        else:
            candidates = []
            for i in committed_units:
                unit = units_info[i]
                if unit['u_i_0'] == 1:
                    p_min = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                else:
                    p_min = unit['p_min_i']
                if p[i] > p_min:
                    cost = unit['b_i'] + 2 * unit['c_i'] * p[i]
                    candidates.append((cost, i, p[i] - p_min))
            if not candidates:
                break
            candidates.sort(key=lambda x: x[0], reverse=True)
            best_i = candidates[0][1]
            step = min(-deficit, candidates[0][2], units_info[best_i]['p_down_i'] if units_info[best_i]['u_i_0'] == 1 else float('inf'))
            p[best_i] -= step
            deficit += step

    for i in flexible:
        if i not in committed_flexible:
            u[i] = 0
            p[i] = 0

    return np.vstack((u, p))
