        total_capacity += min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])

    committed_flexible = []
    for unit in flexible:
        if total_capacity >= load:
            break
        max_output = min(unit['p_max_i'], unit['p_start_i']) if unit['u_i_0'] == 0 else min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
