import heapq

def improved_rolling_commitment_dispatch(units_info, load):
    n_units = len(units_info)
    u = np.zeros(n_units)
    p = np.zeros(n_units)

    must_run = []
    must_off = []
    flexible = []

    for i, unit in enumerate(units_info):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_run.append(i)
            else:
                flexible.append(i)
        else:
            if -unit['t_i_0'] < unit['t_off_min_i']:
                must_off.append(i)
            else:
                flexible.append(i)

    committed = []
    total_min_cap = 0
    total_max_cap = 0

    for i in must_run:
        unit = units_info[i]
        u[i] = 1
        min_p = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        max_p = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        p[i] = min_p
        total_min_cap += min_p
        total_max_cap += max_p
        committed.append(i)

    for i in must_off:
        u[i] = 0
        p[i] = 0

    def avg_cost(unit):
        if unit['u_i_0'] == 0:
            return (unit['s_i'] + unit['a_i'] + unit['b_i'] * unit['p_max_i'] + unit['c_i'] * unit['p_max_i']**2) / unit['p_max_i']
        else:
            return (unit['a_i'] + unit['b_i'] * unit['p_max_i'] + unit['c_i'] * unit['p_max_i']**2) / unit['p_max_i']

    flexible_sorted = sorted(flexible, key=lambda i: avg_cost(units_info[i]))

    for i in flexible_sorted:
        if total_max_cap >= load:
            break
        unit = units_info[i]
        if unit['u_i_0'] == 0:
            max_p = min(unit['p_max_i'], unit['p_start_i'])
        else:
            max_p = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        min_p = unit['p_min_i']
        if unit['u_i_0'] == 1:
            min_p = max(min_p, unit['p_i_0'] - unit['p_down_i'])

        total_max_cap += max_p
        total_min_cap += min_p
        u[i] = 1
        p[i] = min_p
        committed.append(i)

    total_output = np.sum(p)
    deficit = load - total_output

    step = 1.0
    while abs(deficit) > 1e-8:
        if deficit > 0:
            heap = []
            for i in committed:
                unit = units_info[i]
                current_p = p[i]
                if unit['u_i_0'] == 0:
                    max_p = min(unit['p_max_i'], unit['p_start_i'])
                else:
                    max_p = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])

                if current_p < max_p - 1e-8:
                    marginal_cost = unit['b_i'] + 2 * unit['c_i'] * current_p
                    heapq.heappush(heap, (marginal_cost, i))

            if not heap:
                break

            _, idx = heapq.heappop(heap)
            unit = units_info[idx]
            if unit['u_i_0'] == 0:
                max_inc = min(unit['p_max_i'], unit['p_start_i']) - p[idx]
            else:
                max_inc = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i']) - p[idx]

            inc = min(deficit, max_inc, step)
            p[idx] += inc
            total_output += inc
            deficit -= inc
            step = max(0.1, step * 0.99)
        else:
            heap = []
            for i in committed:
                unit = units_info[i]
                current_p = p[i]
                if unit['u_i_0'] == 1:
                    min_p = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                else:
                    min_p = unit['p_min_i']

                if current_p > min_p + 1e-8:
                    marginal_cost = unit['b_i'] + 2 * unit['c_i'] * current_p
                    heapq.heappush(heap, (-marginal_cost, i))

            if not heap:
                break

            _, idx = heapq.heappop(heap)
            unit = units_info[idx]
            if unit['u_i_0'] == 1:
                min_dec = p[idx] - max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            else:
                min_dec = p[idx] - unit['p_min_i']

            dec = min(-deficit, min_dec, step)
            p[idx] -= dec
            total_output -= dec
            deficit += dec
            step = max(0.1, step * 0.99)

    for i in range(n_units):
        if u[i] == 0:
            p[i] = 0.0

    return np.array([u, p])

import numpy as np
import copy

def adaptive_marginal_cost_commitment(units_info, load):
    units = copy.deepcopy(units_info)
    n_units = len(units)
    must_run = []
    must_off = []
    flexible = []

    for i, unit in enumerate(units):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_run.append(unit)
            else:
                flexible.append(unit)
        else:
            if unit['t_i_0'] > -unit['t_off_min_i']:
                must_off.append(unit)
            else:
                flexible.append(unit)

    total_output = 0
    for unit in must_off:
        unit['u_i'] = 0
        unit['p_i'] = 0

    for unit in must_run:
        unit['u_i'] = 1
        min_feasible = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        unit['p_i'] = min_feasible
        total_output += min_feasible

    remaining_load = load - total_output

    def calculate_cost(unit):
        if unit['u_i_0'] == 0:
            avg_cost = (unit['s_i'] + unit['b_i'] * unit['p_max_i'] + unit['c_i'] * unit['p_max_i']**2) / unit['p_max_i']
            marg_cost = unit['b_i'] + 2 * unit['c_i'] * unit['p_min_i']
            return 0.7 * avg_cost + 0.3 * marg_cost
        else:
            return unit['b_i'] + 2 * unit['c_i'] * unit['p_min_i']

    flexible.sort(key=calculate_cost)

    committed_flexible = []
    for unit in flexible:
        if remaining_load <= 0:
            break
        unit['u_i'] = 1
        if unit['u_i_0'] == 0:
            unit['p_i'] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        else:
            unit['p_i'] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        committed_flexible.append(unit)
        max_capacity = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i']) if unit['u_i_0'] == 1 else unit['p_start_i']
        remaining_load -= max_capacity

    committed_units = must_run + committed_flexible

    total_output = sum(unit['p_i'] for unit in committed_units)
    deficit = load - total_output

    def get_feasible_range(unit):
        if unit['u_i_0'] == 1:
            low = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            high = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            low = unit['p_min_i']
            high = min(unit['p_max_i'], unit['p_start_i'])
        return low, high

    while abs(deficit) > 1e-5:
        if deficit > 0:
            candidates = []
            for unit in committed_units:
                low, high = get_feasible_range(unit)
                if unit['p_i'] < high:
                    headroom = high - unit['p_i']
                    marginal_cost = unit['b_i'] + 2 * unit['c_i'] * unit['p_i']
                    step = min(headroom, deficit, max(1.0, deficit * 0.1))
                    candidates.append((marginal_cost, step, unit))
            if not candidates:
                break
            candidates.sort(key=lambda x: x[0])
            best_candidate = candidates[0]
            best_candidate[2]['p_i'] += best_candidate[1]
            deficit -= best_candidate[1]
        else:
            candidates = []
            for unit in committed_units:
                low, high = get_feasible_range(unit)
                if unit['p_i'] > low:
                    room = unit['p_i'] - low
                    marginal_cost = unit['b_i'] + 2 * unit['c_i'] * unit['p_i']
                    step = min(room, -deficit, max(1.0, -deficit * 0.1))
                    candidates.append((marginal_cost, step, unit))
            if not candidates:
                break
            candidates.sort(key=lambda x: x[0], reverse=True)
            best_candidate = candidates[0]
            best_candidate[2]['p_i'] -= best_candidate[1]
            deficit += best_candidate[1]

    for unit in flexible:
        if unit not in committed_flexible:
            unit['u_i'] = 0
            unit['p_i'] = 0

    schedules = np.zeros((2, n_units))
    for i, unit in enumerate(units):
        schedules[0, i] = unit['u_i']
        schedules[1, i] = unit['p_i']

    return schedules
