def enhanced_rolling_priority_dispatch(units_info, load):
    n_units = len(units_info)
    u = np.zeros(n_units)
    p = np.zeros(n_units)

    # Determine must-run and must-off units
    must_run = []
    must_off = []
    for i, unit in enumerate(units_info):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_run.append(i)
        else:
            if unit['t_i_0'] > -unit['t_off_min_i']:
                must_off.append(i)

    # Initialize commitments
    for i in must_run:
        u[i] = 1
    for i in must_off:
        u[i] = 0

    # Calculate available capacity from must-run units
    total_min = 0
    total_max = 0
    for i in must_run:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            p_min = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            p_max = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            p_min = unit['p_min_i']
            p_max = min(unit['p_max_i'], unit['p_start_i'])
        total_min += p_min
        total_max += p_max

    # Sort available units by cost criteria
    available_units = []
    for i in range(n_units):
        if i not in must_run and i not in must_off:
            unit = units_info[i]
            marginal_cost = 2 * unit['c_i'] * unit['p_min_i'] + unit['b_i']
            avg_startup_cost = unit['s_i'] / unit['p_max_i'] if unit['p_max_i'] > 0 else float('inf')
            priority = marginal_cost + avg_startup_cost
            available_units.append((priority, i))

    available_units.sort(key=lambda x: x[0])

    # Commit additional units if needed
    if total_max < load:
        for _, i in available_units:
            if total_max >= load:
                break
            u[i] = 1
            unit = units_info[i]
            if unit['u_i_0'] == 1:
                p_max = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                p_max = min(unit['p_max_i'], unit['p_start_i'])
            total_max += p_max

    # Economic dispatch with ramp constraints
    committed = [i for i in range(n_units) if u[i] == 1]
    # Set initial outputs to minimum feasible
    for i in committed:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            p[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        else:
            p[i] = unit['p_min_i']

    total_p = sum(p)

    # Lambda iteration for dispatch
    max_iter = 100
    tolerance = 1e-6
    for _ in range(max_iter):
        # Calculate marginal costs
        marginal_costs = [2 * units_info[i]['c_i'] * p[i] + units_info[i]['b_i'] for i in committed]
        lambda_val = sum(marginal_costs) / len(committed) if committed else 0

        # Adjust outputs
        for i in committed:
            unit = units_info[i]
            # Calculate feasible range
            if unit['u_i_0'] == 1:
                p_low = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                p_high = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                p_low = unit['p_min_i']
                p_high = min(unit['p_max_i'], unit['p_start_i'])

            # Calculate unconstrained output
            p_unconstrained = (lambda_val - unit['b_i']) / (2 * unit['c_i']) if unit['c_i'] > 0 else p_high
            new_p = max(p_low, min(p_high, p_unconstrained))

            # Update total power
            total_p = total_p - p[i] + new_p
            p[i] = new_p

        if abs(total_p - load) < tolerance:
            break

    # Fine-tuning to match load exactly
    diff = load - total_p
    while abs(diff) > tolerance:
        # Find units to adjust
        if diff > 0:
            # Increase output of unit with smallest marginal cost
            candidates = []
            for i in committed:
                unit = units_info[i]
