                    p_high = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
                else:
                    p_high = min(unit['p_max_i'], unit['p_start_i'])
                if p[i] < p_high:
                    marginal_cost = 2 * unit['c_i'] * p[i] + unit['b_i']
                    candidates.append((marginal_cost, i))
            if not candidates:
                break
            candidates.sort(key=lambda x: x[0])
            i = candidates[0][1]
            unit = units_info[i]
            if unit['u_i_0'] == 1:
                p_high = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                p_high = min(unit['p_max_i'], unit['p_start_i'])
            max_increase = min(diff, p_high - p[i])
            p[i] += max_increase
            diff -= max_increase
        else:
            # Decrease output of unit with largest marginal cost
            candidates = []
            for i in committed:
                unit = units_info[i]
                if unit['u_i_0'] == 1:
                    p_low = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                else:
                    p_low = unit['p_min_i']
                if p[i] > p_low:
                    marginal_cost = 2 * unit['c_i'] * p[i] + unit['b_i']
                    candidates.append((marginal_cost, i))
            if not candidates:
