    available_units = []
    for i, unit in enumerate(units_info):
        if not committed[i] and max_outputs[i] > 0:
            avg_cost = (unit['b_i'] * unit['p_max_i'] + unit['c_i'] * unit['p_max_i']**2 + unit['s_i']) / unit['p_max_i']
            available_units.append((avg_cost, i))

    available_units.sort(key=lambda x: x[0])

    current_capacity = sum(max_outputs[i] for i in range(n_units) if committed[i])
    for _, idx in available_units:
        if current_capacity < load:
            committed[idx] = True
            current_capacity += max_outputs[idx]
        else:
            break

    total_min = sum(min_outputs[i] for i in range(n_units) if committed[i])
    total_max = sum(max_outputs[i] for i in range(n_units) if committed[i])

    if total_min > load:
        for i in range(n_units):
            if committed[i] and not must_run[i]:
                committed[i] = False
                total_min -= min_outputs[i]
                if total_min <= load:
                    break
        if total_min > load:
            for i in range(n_units):
                if committed[i] and must_run[i]:
                    schedules[0][i] = 1
                    schedules[1][i] = min_outputs[i]
            return schedules

    outputs = [min_outputs[i] if committed[i] else 0 for i in range(n_units)]
    remaining_load = load - total_min

    while remaining_load > 1e-6:
        best_marginal = float('inf')
        best_idx = -1
        for i in range(n_units):
            if committed[i] and outputs[i] < max_outputs[i]:
                marginal = units_info[i]['b_i'] + 2 * units_info[i]['c_i'] * outputs[i]
                if marginal < best_marginal:
                    best_marginal = marginal
                    best_idx = i
        if best_idx == -1:
            break

        add_cap = min(remaining_load, max_outputs[best_idx] - outputs[best_idx])
        outputs[best_idx] += add_cap
        remaining_load -= add_cap

    for i in range(n_units):
        schedules[0][i] = 1 if committed[i] else 0
        schedules[1][i] = outputs[i] if committed[i] else 0

    return schedules

import numpy as np

def commit_units_by_marginal_cost(units_info, load):
    n_units = len(units_info)
    u = [0] * n_units
    p = [0] * n_units
    must_on = []
    must_off = []
    flexible = []

    for i, unit in enumerate(units_info):
        u_i_0 = unit['u_i_0']
        t_i_0 = unit['t_i_0']
        t_on_min_i = unit['t_on_min_i']
        t_off_min_i = unit['t_off_min_i']
        p_i_0 = unit['p_i_0']
        p_shut_i = unit['p_shut_i']

        if u_i_0 == 1:
            if t_i_0 < t_on_min_i or p_i_0 > p_shut_i:
                must_on.append(i)
            else:
                flexible.append(i)
        else:
            if abs(t_i_0) < t_off_min_i:
                must_off.append(i)
            else:
                flexible.append(i)

    for i in must_on:
        u[i] = 1
    for i in must_off:
        u[i] = 0

    total_min_cap = 0
    total_max_cap = 0
    bounds = {}

    for i in must_on:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            lb = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            ub = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            lb = unit['p_min_i']
            ub = min(unit['p_max_i'], unit['p_start_i'])
        bounds[i] = (lb, ub)
        total_min_cap += lb
        total_max_cap += ub

    flexible_sorted = []
    for i in flexible:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            cost_per_mw = (unit['a_i'] + unit['b_i']*unit['p_max_i'] + unit['c_i']*unit['p_max_i']**2) / unit['p_max_i']
        else:
            cost_per_mw = (unit['a_i'] + unit['b_i']*unit['p_max_i'] + unit['c_i']*unit['p_max_i']**2 + unit['s_i']) / unit['p_max_i']
        flexible_sorted.append((cost_per_mw, i))

    flexible_sorted.sort(key=lambda x: x[0])

    for cost, i in flexible_sorted:
        if total_max_cap >= load:
            break
        unit = units_info[i]
        u[i] = 1
        if unit['u_i_0'] == 1:
            lb = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            ub = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            lb = unit['p_min_i']
            ub = min(unit['p_max_i'], unit['p_start_i'])
        bounds[i] = (lb, ub)
        total_min_cap += lb
        total_max_cap += ub

    committed = [i for i in range(n_units) if u[i] == 1]
    for i in committed:
        p[i] = bounds[i][0]

    remaining = load - sum(p)
    if remaining < 0:
        remaining = 0

    active_units = committed.copy()
    while remaining > 0 and active_units:
        best_unit = None
        best_marginal = float('inf')
        for i in active_units:
            unit = units_info[i]
            marginal = unit['b_i'] + 2 * unit['c_i'] * p[i]
            if marginal < best_marginal and p[i] < bounds[i][1]:
                best_marginal = marginal
                best_unit = i

        if best_unit is None:
            break

        unit = units_info[best_unit]
        max_add = bounds[best_unit][1] - p[best_unit]
        add = min(remaining, max_add)
        p[best_unit] += add
        remaining -= add

        if p[best_unit] >= bounds[best_unit][1]:
            active_units.remove(best_unit)

    schedules = np.array([u, p])
    return schedules
