import numpy as np

def commit_units_by_adaptive_priority(units_info, load):
    n_units = len(units_info)
    u = np.zeros(n_units)
    p = np.zeros(n_units)
    must_run = []
    must_off = []
    flexible = []

    for i, unit in enumerate(units_info):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_run.append(i)
            else:
                flexible.append(i)
        else:
            if -unit['t_i_0'] < unit['t_off_min_i']:
                must_off.append(i)
            else:
                flexible.append(i)

    priorities = []
    for i in flexible:
        unit = units_info[i]
        marginal_cost = unit['b_i'] + 2 * unit['c_i'] * unit['p_min_i']
        priority = marginal_cost + unit['s_i'] / unit['p_max_i'] if unit['u_i_0'] == 0 else marginal_cost
        priorities.append((priority, i))

    priorities.sort(key=lambda x: x[0])
    sorted_flexible = [i for _, i in priorities]

    committed = must_run[:]
    total_max = sum(min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i']) if unit['u_i_0'] == 1 else unit['p_start_i'] for unit in [units_info[i] for i in committed])

    for i in sorted_flexible:
        if total_max >= load:
            break
        unit = units_info[i]
        committed.append(i)
        total_max += unit['p_start_i'] if unit['u_i_0'] == 0 else min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])

    L = np.zeros(n_units)
    U = np.zeros(n_units)
    for i in committed:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            L[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            U[i] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            L[i] = unit['p_min_i']
            U[i] = unit['p_start_i']

    for i in committed:
        p[i] = L[i]

    remaining = load - np.sum(p)
    units_to_adjust = [i for i in committed if p[i] < U[i]]

    while remaining > 0 and units_to_adjust:
        marginal_costs = []
        for i in units_to_adjust:
            unit = units_info[i]
            marginal_costs.append((unit['b_i'] + 2 * unit['c_i'] * p[i], i))
        marginal_costs.sort(key=lambda x: x[0])
        best_unit = marginal_costs[0][1]
        increase = min(remaining, U[best_unit] - p[best_unit])
        p[best_unit] += increase
        remaining -= increase
        if p[best_unit] >= U[best_unit]:
            units_to_adjust.remove(best_unit)

    for i in committed:
        u[i] = 1

    return np.array([u, p])

