def enhanced_commitment_dispatch(units_info, load):
    units = units_info
    n_units = len(units)

    # Initialize output arrays
    u = np.zeros(n_units)
    p = np.zeros(n_units)

    # Step 1: Identify must-run and must-off units
    must_run = []
    must_off = []
    for i, unit in enumerate(units):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_run.append(i)
        else:
            if unit['t_i_0'] > -unit['t_off_min_i']:
                must_off.append(i)

    # Step 2: Calculate feasible power ranges
    p_min_feasible = np.zeros(n_units)
    p_max_feasible = np.zeros(n_units)
    for i, unit in enumerate(units):
        if unit['u_i_0'] == 1:
            p_min_feasible[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            p_max_feasible[i] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            p_min_feasible[i] = unit['p_min_i']
            p_max_feasible[i] = min(unit['p_max_i'], unit['p_start_i'])

    # Step 3: Calculate full incremental costs
    full_costs = []
    for i, unit in enumerate(units):
        if i in must_off:
            continue
        min_p = p_min_feasible[i]
        incremental_cost = unit['b_i'] + 2 * unit['c_i'] * min_p
        startup_cost = unit['s_i'] if unit['u_i_0'] == 0 else 0
        full_cost = incremental_cost + startup_cost / min_p if min_p > 0 else float('inf')
        full_costs.append((full_cost, i))

    full_costs.sort(key=lambda x: x[0])

    # Step 4: Priority-based commitment
    committed = set(must_run)
    total_min = sum(p_min_feasible[i] for i in committed)
    total_max = sum(p_max_feasible[i] for i in committed)

    for cost, i in full_costs:
        if i not in committed and i not in must_off:
            if total_max < load:
                committed.add(i)
                total_min += p_min_feasible[i]
                total_max += p_max_feasible[i]
            else:
                break

    # Adjust if overcommitted
    while total_min > load:
        candidate = -1
        highest_cost = -1
        for i in committed:
            if i not in must_run:
                cost = units[i]['b_i'] + 2 * units[i]['c_i'] * p_min_feasible[i]
                if cost > highest_cost:
                    highest_cost = cost
                    candidate = i
        if candidate == -1:
            break
        committed.remove(candidate)
        total_min -= p_min_feasible[candidate]
        total_max -= p_max_feasible[candidate]

    # Step 5: Economic dispatch using bisection
    lambda_low = 0
    lambda_high = 1000
    tol = 1e-6
    committed_list = list(committed)

    for _ in range(100):
        lambda_mid = (lambda_low + lambda_high) / 2
        total_p = 0
        for i in committed_list:
            unit = units[i]
            p_i = (lambda_mid - unit['b_i']) / (2 * unit['c_i']) if unit['c_i'] > 0 else p_max_feasible[i]
            p_i = max(p_min_feasible[i], min(p_max_feasible[i], p_i))
            total_p += p_i

        if abs(total_p - load) < tol:
            break
        elif total_p < load:
            lambda_low = lambda_mid
        else:
            lambda_high = lambda_mid

    # Assign power outputs
    for i in committed_list:
        unit = units[i]
        p_i = (lambda_mid - unit['b_i']) / (2 * unit['c_i']) if unit['c_i'] > 0 else p_max_feasible[i]
        p[i] = max(p_min_feasible[i], min(p_max_feasible[i], p_i))
        u[i] = 1

    # Step 6: Fine-tune to match load exactly
    total_p = sum(p)
    diff = load - total_p
    while abs(diff) > tol:
        candidates = []
        for i in committed_list:
            if diff > 0 and p[i] < p_max_feasible[i]:
                candidates.append((units[i]['b_i'] + 2 * units[i]['c_i'] * p[i], i))
            elif diff < 0 and p[i] > p_min_feasible[i]:
                candidates.append((units[i]['b_i'] + 2 * units[i]['c_i'] * p[i], i))

        if not candidates:
            break

        candidates.sort(key=lambda x: x[0] if diff > 0 else -x[0])
        i = candidates[0][1]
        if diff > 0:
            p[i] = min(p_max_feasible[i], p[i] + min(diff, units[i]['p_up_i']))
        else:
            p[i] = max(p_min_feasible[i], p[i] + max(diff, -units[i]['p_down_i']))
        total_p = sum(p)
        diff = load - total_p

    # Step 7: Validate and adjust constraints
    for i in range(n_units):
        if u[i] == 1:
            if p[i] < p_min_feasible[i] or p[i] > p_max_feasible[i]:
                u[i] = 0
                p[i] = 0

    return np.array([u, p])

