def rolling_uc_heuristic_improved(units_info, load):
    n_units = len(units_info)
    u = np.zeros(n_units)
    p = np.zeros(n_units)

    must_on = np.zeros(n_units, dtype=bool)
    must_off = np.zeros(n_units, dtype=bool)
    adjustable = np.zeros(n_units, dtype=bool)

    for i, unit in enumerate(units_info):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_on[i] = True
            else:
                adjustable[i] = True
        else:
            if unit['t_i_0'] > -unit['t_off_min_i']:
                must_off[i] = True
            else:
                adjustable[i] = True

    for i in range(n_units):
        if must_on[i]:
            u[i] = 1
        elif must_off[i]:
            u[i] = 0
        else:
            u[i] = units_info[i]['u_i_0']

    committed = np.where(u == 1)[0]
    available = np.where(adjustable)[0]

    total_min = 0
    total_max = 0
    low_bound = np.zeros(n_units)
    high_bound = np.zeros(n_units)

    for i in committed:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            low_bound[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            high_bound[i] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            low_bound[i] = unit['p_min_i']
            high_bound[i] = min(unit['p_max_i'], unit['p_start_i'])
        total_min += low_bound[i]
        total_max += high_bound[i]

    if total_min > load:
        off_candidates = [i for i in available if u[i] == 1]
        off_candidates.sort(key=lambda i: units_info[i]['b_i'] + 2*units_info[i]['c_i']*low_bound[i], reverse=True)

        for i in off_candidates:
            if total_min <= load:
                break
            u[i] = 0
            total_min -= low_bound[i]
            total_max -= high_bound[i]

    if total_max < load:
        on_candidates = [i for i in available if u[i] == 0]
        on_candidates.sort(key=lambda i: (units_info[i]['a_i'] + units_info[i]['b_i']*units_info[i]['p_min_i'] + 
                                         units_info[i]['c_i']*units_info[i]['p_min_i']**2 + units_info[i]['s_i']) / units_info[i]['p_min_i'])

        for i in on_candidates:
            unit = units_info[i]
            new_low = unit['p_min_i']
            new_high = min(unit['p_max_i'], unit['p_start_i'])
            if total_max + new_high >= load:
                break
            u[i] = 1
            low_bound[i] = new_low
            high_bound[i] = new_high
            total_min += new_low
            total_max += new_high

    committed = np.where(u == 1)[0]
    if not committed.size:
        return np.array([u, p])

    lambda_low = min(units_info[i]['b_i'] + 2*units_info[i]['c_i']*low_bound[i] for i in committed)
    lambda_high = max(units_info[i]['b_i'] + 2*units_info[i]['c_i']*high_bound[i] for i in committed)

    for _ in range(100):
        lambda_mid = (lambda_low + lambda_high) / 2
        total_p = 0
        for i in committed:
            unit = units_info[i]
            p_i = (lambda_mid - unit['b_i']) / (2 * unit['c_i'])
            p_i = max(low_bound[i], min(high_bound[i], p_i))
            total_p += p_i

        if abs(total_p - load) < 1e-6:
            break
        elif total_p > load:
            lambda_high = lambda_mid
        else:
            lambda_low = lambda_mid

    for i in committed:
        unit = units_info[i]
        p_i = (lambda_mid - unit['b_i']) / (2 * unit['c_i'])
        p[i] = max(low_bound[i], min(high_bound[i], p_i))

    total_p = np.sum(p)
    residual = load - total_p
    while abs(residual) > 1e-6:
        if residual > 0:
            candidates = [i for i in committed if p[i] < high_bound[i]]
            if not candidates:
                break
            i = min(candidates, key=lambda i: units_info[i]['b_i'] + 2*units_info[i]['c_i']*p[i])
            p[i] = min(p[i] + residual, high_bound[i])
        else:
            candidates = [i for i in committed if p[i] > low_bound[i]]
            if not candidates:
                break
            i = max(candidates, key=lambda i: units_info[i]['b_i'] + 2*units_info[i]['c_i']*p[i])
            p[i] = max(p[i] + residual, low_bound[i])
        residual = load - np.sum(p)

    return np.array([u, p])
