import numpy as np

def hybrid_adaptive_priority_commitment(units_info, load):
    current_load = load[0]
    n_units = len(units_info)
    must_on = []
    must_off = []

    # Step 1: Identify must-on and must-off units
    for unit in units_info:
        if unit['u_i_0'] == 1:
            must_on.append(unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i'])
            must_off.append(False)
        else:
            must_on.append(False)
            must_off.append(-unit['t_i_0'] < unit['t_off_min_i'] if unit['t_i_0'] < 0 else False)

    # Step 2: Initialize commitment states
    u_i = []
    for i, unit in enumerate(units_info):
        if must_on[i]:
            u_i.append(1)
        elif must_off[i]:
            u_i.append(0)
        else:
            u_i.append(unit['u_i_0'])

    # Step 3: Compute feasible power ranges
    min_p_i = [0.0] * n_units
    max_p_i = [0.0] * n_units

    for i, unit in enumerate(units_info):
        if u_i[i] == 1:
            if unit['u_i_0'] == 1:
                min_p_i[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                max_p_i[i] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                min_p_i[i] = unit['p_min_i']
                max_p_i[i] = min(unit['p_max_i'], unit['p_start_i'])

    total_min = sum(min_p_i)
    total_max = sum(max_p_i)

    # Step 4: Adjust commitment states
    # Under-committed case: activate units
    if current_load > total_max + 1e-5:
        candidates = [i for i in range(n_units) if u_i[i] == 0 and not must_off[i]]
        metrics = [
            (unit['s_i'] + unit['a_i'] + unit['b_i'] * unit['p_min_i'] + unit['c_i'] * unit['p_min_i']**2) / unit['p_min_i']
            for i, unit in enumerate(units_info) if i in candidates
        ]
        sorted_candidates = [i for _, i in sorted(zip(metrics, candidates))]

        for i in sorted_candidates:
            if total_max >= current_load:
                break
            u_i[i] = 1
            min_p_i[i] = units_info[i]['p_min_i']
            max_p_i[i] = min(units_info[i]['p_max_i'], units_info[i]['p_start_i'])
            total_min += min_p_i[i]
            total_max += max_p_i[i]

    # Over-committed case: decommit units
    elif current_load < total_min - 1e-5:
        candidates = [i for i in range(n_units) if u_i[i] == 1 and not must_on[i]]
        metrics = [
            unit['a_i'] + unit['b_i'] * min_p_i[i] + unit['c_i'] * min_p_i[i]**2
            for i in candidates
        ]
        sorted_candidates = [i for _, i in sorted(zip(metrics, candidates), key=lambda x: x[0], reverse=True)]

        for i in sorted_candidates:
            if total_min <= current_load:
                break
            u_i[i] = 0
            total_min -= min_p_i[i]
            total_max -= max_p_i[i]
            min_p_i[i] = max_p_i[i] = 0.0
    else:  # Adequate system: optimize by decommitting expensive units
        candidates = [i for i in range(n_units) if u_i[i] == 1 and not must_on[i]]
        metrics = [
            unit['a_i'] + unit['b_i'] * min_p_i[i] + unit['c_i'] * min_p_i[i]**2
            for i, unit in enumerate(units_info) if i in candidates
        ]
        sorted_candidates = [i for _, i in sorted(zip(metrics, candidates), key=lambda x: x[0], reverse=True)]

        for i in sorted_candidates:
            new_min = total_min - min_p_i[i]
            new_max = total_max - max_p_i[i]
            if new_min <= current_load <= new_max:
                u_i[i] = 0
                total_min, total_max = new_min, new_max
                min_p_i[i] = max_p_i[i] = 0.0

    # Step 5: Economic dispatch
    committed_units = [i for i in range(n_units) if u_i[i] == 1]
    if not committed_units:
        return np.array([u_i, [0.0] * n_units])

    if abs(current_load - total_min) <= 1e-5:
        p_i = [min_p_i[i] if u_i[i] == 1 else 0.0 for i in range(n_units)]
        return np.array([u_i, p_i])
    elif abs(current_load - total_max) <= 1e-5:
        p_i = [max_p_i[i] if u_i[i] == 1 else 0.0 for i in range(n_units)]
        return np.array([u_i, p_i])

    b_list = [units_info[i]['b_i'] for i in committed_units]
    c_list = [units_info[i]['c_i'] for i in committed_units]
    min_list = [min_p_i[i] for i in committed_units]
    max_list = [max_p_i[i] for i in committed_units]

    # Lambda iteration setup
    mc_min_list = [b_list[i] + 2 * c_list[i] * min_list[i] for i in range(len(committed_units))]
    mc_max_list = [b_list[i] + 2 * c_list[i] * max_list[i] for i in range(len(committed_units))]
    lambda_low = min(mc_min_list)
    lambda_high = max(mc_max_list)

    # Bisection method
    tol_p = 0.1
    max_iter = 100
    for _ in range(max_iter):
        lam = (lambda_low + lambda_high) / 2.0
        total_p = 0.0
        for i in range(len(committed_units)):
            if c_list[i] == 0:
                p = min_list[i]
            else:
                p = (lam - b_list[i]) / (2 * c_list[i])
                p = max(min_list[i], min(p, max_list[i]))
            total_p += p

        if abs(total_p - current_load) < tol_p:
            break
        if total_p < current_load:
            lambda_low = lam
        else:
            lambda_high = lam

    # Calculate final dispatch
    p_list = []
    for i in range(len(committed_units)):
        if c_list[i] == 0:
            p = min_list[i]
        else:
            p = (lam - b_list[i]) / (2 * c_list[i])
            p = max(min_list[i], min(p, max_list[i]))
        p_list.append(p)

    # Build output arrays
    p_i = [0.0] * n_units
    for idx, unit_idx in enumerate(committed_units):
        p_i[unit_idx] = p_list[idx]

    return np.array([u_i, p_i])
