def economic_dispatch(online_units, load):
    n = len(online_units)
    lb = []
    ub = []
    indices = []
    for unit in online_units:
        if unit['u_i_0'] == 1:
            lb_i = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            ub_i = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            lb_i = unit['p_min_i']
            ub_i = min(unit['p_max_i'], unit['p_start_i'])
        lb.append(lb_i)
        ub.append(ub_i)
        indices.append(unit['index'])

    total_lb = sum(lb)
    total_ub = sum(ub)

    if total_lb > load or total_ub < load:
        return None, None

    units_dispatch = []
    for i, unit in enumerate(online_units):
        mc = unit['b_i'] + 2 * unit['c_i'] * lb[i]
        units_dispatch.append((mc, lb[i], ub[i], i, unit['index']))

    units_dispatch.sort(key=lambda x: x[0])

    p = lb.copy()
    total = total_lb
    gap = load - total

    for i in range(len(units_dispatch)):
        mc, lb_i, ub_i, idx, unit_idx = units_dispatch[i]
        add = min(gap, ub_i - lb_i)
        p[idx] += add
        gap -= add
        if gap <= 1e-5:
            break

    if gap > 1e-5:
        return None, None

    dispatch_dict = {}
    for i in range(len(online_units)):
        dispatch_dict[indices[i]] = p[i]

    total_cost = 0
    for i, unit in enumerate(online_units):
        if unit['u_i_0'] == 0:
            total_cost += unit['s_i']
        total_cost += unit['a_i'] + unit['b_i'] * p[i] + unit['c_i'] * (p[i] ** 2)

    return dispatch_dict, total_cost

