                must_on.append(i)
            else:
                free_on.append(i)
        else:
            if unit['t_i_0'] < 0 and abs(unit['t_i_0']) < unit['t_off_min_i']:
                must_off.append(i)
            else:
                free_off.append(i)

    online_set = must_on + free_on
    online_units = [{'index': i, **units_info[i]} for i in online_set]
    dispatch_result, _ = economic_dispatch(online_units, current_load)

    if dispatch_result is None:
        candidates = []
        for i in free_off:
            unit = units_info[i]
            key = unit['s_i'] / unit['p_max_i'] + (unit['b_i'] + 2 * unit['c_i'] * unit['p_min_i'])
            candidates.append((key, i))
        candidates.sort(key=lambda x: x[0])

        for key, i in candidates:
            online_set.append(i)
            online_units = [{'index': j, **units_info[j]} for j in online_set]
            dispatch_result, _ = economic_dispatch(online_units, current_load)
            if dispatch_result is not None:
                break

        if dispatch_result is None:
            dispatch_result = {}
            for i in online_set:
                if units_info[i]['u_i_0'] == 1:
                    ub_i = min(units_info[i]['p_max_i'], units_info[i]['p_i_0'] + units_info[i]['p_up_i'])
                else:
                    ub_i = min(units_info[i]['p_max_i'], units_info[i]['p_start_i'])
                dispatch_result[i] = ub_i

    else:
        candidates = []
        for i in free_on:
            if i in online_set and i in dispatch_result:
                if units_info[i]['u_i_0'] == 1:
                    lb_i = max(units_info[i]['p_min_i'], units_info[i]['p_i_0'] - units_info[i]['p_down_i'])
                else:
                    lb_i = units_info[i]['p_min_i']
                if abs(dispatch_result[i] - lb_i) < 1e-5:
                    saving = units_info[i]['a_i'] + units_info[i]['b_i'] * dispatch_result[i] + units_info[i]['c_i'] * (dispatch_result[i] ** 2)
                    candidates.append((saving, i))

        candidates.sort(key=lambda x: x[0], reverse=True)
        for saving, i in candidates:
            new_online_set = [j for j in online_set if j != i]
            new_online_units = [{'index': j, **units_info[j]} for j in new_online_set]
            new_dispatch, _ = economic_dispatch(new_online_units, current_load)
            if new_dispatch is not None:
                online_set = new_online_set
                dispatch_result = new_dispatch
                break

    schedules = np.zeros((2, n_units))
    for i in range(n_units):
        if i in online_set and i in dispatch_result:
            schedules[0, i] = 1
            schedules[1, i] = dispatch_result[i]
        else:
            schedules[0, i] = 0
            schedules[1, i] = 0

    return schedules

