def economic_dispatch(b_list, c_list, bounds, load):
    total_min = sum(bound[0] for bound in bounds)
    total_max = sum(bound[1] for bound in bounds)
    n_units = len(b_list)
    if total_min >= load:
        return [bound[0] for bound in bounds]
    if total_max <= load:
        return [bound[1] for bound in bounds]

    inc_min = []
    inc_max = []
    for i in range(n_units):
        if c_list[i] > 0:
            inc_min.append(b_list[i] + 2 * c_list[i] * bounds[i][0])
            inc_max.append(b_list[i] + 2 * c_list[i] * bounds[i][1])
        else:
            inc_min.append(b_list[i])
            inc_max.append(b_list[i])

    lambda_low = min(inc_min)
    lambda_high = max(inc_max)
    tol = 0.1
    max_iter = 100
    p_out = [0] * n_units

    for _ in range(max_iter):
        lm = (lambda_low + lambda_high) / 2.0
        total_p = 0.0
        for i in range(n_units):
            if c_list[i] > 0:
                p_i = (lm - b_list[i]) / (2 * c_list[i])
                if p_i < bounds[i][0]:
                    p_i = bounds[i][0]
                elif p_i > bounds[i][1]:
                    p_i = bounds[i][1]
            else:
                if lm > b_list[i]:
                    p_i = bounds[i][1]
                elif lm < b_list[i]:
                    p_i = bounds[i][0]
                else:
                    p_i = bounds[i][0]
            p_out[i] = p_i
            total_p += p_i

        if abs(total_p - load) < tol:
            break
        if total_p < load:
            lambda_low = lm
        else:
            lambda_high = lm

    return p_out

