def enhanced_hybrid_commitment(units_info, load):
    current_load = load[0]
    num_units = len(units_info)
    must_on = []
    must_off = []
    free_on = []
    free_off = []
    eligible_off = []
    ineligible_off = []

    for i, unit in enumerate(units_info):
        u_i0 = unit['u_i_0']
        t_i0 = unit['t_i_0']
        p_i0 = unit['p_i_0']
        p_shut_i = unit['p_shut_i']
        t_on_min = unit['t_on_min_i']
        t_off_min = unit['t_off_min_i']

        if u_i0 == 1:
            if t_i0 < t_on_min or p_i0 > p_shut_i:
                must_on.append(i)
            else:
                if p_i0 <= unit['p_down_i']:
                    eligible_off.append(i)
                else:
                    ineligible_off.append(i)
        else:
            if -t_i0 < t_off_min:
                must_off.append(i)
            else:
                free_off.append(i)

    must_on.extend(ineligible_off)
    free_on = eligible_off
    committed = set(must_on + free_on)

    total_min = 0.0
    total_max = 0.0
    min_outputs = {}
    max_outputs = {}

    for i in committed:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            min_out = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            max_out = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            min_out = unit['p_min_i']
            max_out = min(unit['p_max_i'], unit['p_start_i'], unit['p_up_i'])
        min_outputs[i] = min_out
        max_outputs[i] = max_out
        total_min += min_out
        total_max += max_out

    sorted_free_on = sorted(
        free_on,
        key=lambda i: min_outputs[i],
        reverse=True
    )
    for i in sorted_free_on:
        if total_min > current_load and total_max - max_outputs[i] >= current_load:
            committed.remove(i)
            total_min -= min_outputs[i]
            total_max -= max_outputs[i]

    if total_max < current_load:
        sorted_free_off = sorted(
            free_off,
            key=lambda i: (units_info[i]['s_i'] + units_info[i]['a_i'] + 
                          units_info[i]['b_i'] * units_info[i]['p_min_i'] + 
                          units_info[i]['c_i'] * units_info[i]['p_min_i']**2) / units_info[i]['p_min_i']
        )
        for i in sorted_free_off:
            if total_max < current_load:
                committed.add(i)
                unit = units_info[i]
                min_out = unit['p_min_i']
                max_out = min(unit['p_max_i'], unit['p_start_i'], unit['p_up_i'])
                min_outputs[i] = min_out
                max_outputs[i] = max_out
                total_min += min_out
                total_max += max_out
            else:
                break

    p_vals = {}
    for i in committed:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            p_vals[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
        else:
            p_vals[i] = unit['p_min_i']

    total_output = sum(p_vals.values())
    deficit = current_load - total_output

    while deficit > 1e-6:
        candidates = []
        for i in committed:
            if p_vals[i] < max_outputs[i]:
                inc_cost = units_info[i]['b_i'] + 2 * units_info[i]['c_i'] * p_vals[i]
                headroom = max_outputs[i] - p_vals[i]
                candidates.append((inc_cost, headroom, i))
        if not candidates:
            break
        inc_cost, headroom, i = min(candidates, key=lambda x: x[0])
        increment = min(deficit, headroom)
        p_vals[i] += increment
        total_output += increment
        deficit -= increment

    if total_output - current_load > 1e-6:
        surplus = total_output - current_load
        sorted_committed = sorted(
            committed,
            key=lambda i: units_info[i]['b_i'] + 2 * units_info[i]['c_i'] * p_vals[i],
            reverse=True
        )
        for i in sorted_committed:
            if abs(surplus) < 1e-6:
                break
            min_bound = min_outputs[i]
            decrement = min(surplus, p_vals[i] - min_bound)
            if decrement > 0:
                p_vals[i] -= decrement
                surplus -= decrement

    committed_sorted = sorted(committed)
    free_on_set = set(free_on)
    free_off_set = set(free_off)
    high_cost_free_on = [
        i for i in committed_sorted 
        if i in free_on_set and i not in must_on
    ]
    high_cost_free_on.sort(
        key=lambda i: units_info[i]['a_i'] + units_info[i]['b_i'] * min_outputs[i] + 
                      units_info[i]['c_i'] * min_outputs[i]**2,
        reverse=True
    )

    for i in high_cost_free_on:
        if i not in p_vals:
            continue
        new_min = total_min - min_outputs[i]
        new_max = total_max - max_outputs[i]
        if new_min <= current_load <= new_max:
            committed.remove(i)
            total_min = new_min
            total_max = new_max
            del p_vals[i]

    u_out = np.zeros(num_units)
    p_out = np.zeros(num_units)
    for i in range(num_units):
        if i in p_vals:
            u_out[i] = 1
            p_out[i] = p_vals[i]

    return np.array([u_out, p_out])

