def commit_units_heuristic(units_info, load):
    load_current = load[0]
    n_units = len(units_info)

    must_on = []
    must_off = []
    group1 = []
    group2 = []

    for i, unit in enumerate(units_info):
        u_i0 = unit['u_i_0']
        t_i0 = unit['t_i_0']
        p_i0 = unit['p_i_0']
        t_on_min = unit['t_on_min_i']
        t_off_min = unit['t_off_min_i']
        p_shut = unit['p_shut_i']

        if u_i0 == 1:
            if t_i0 < t_on_min or p_i0 > p_shut:
                must_on.append(i)
        else:
            if t_i0 > -t_off_min:
                must_off.append(i)

    for i in range(n_units):
        if i in must_on or i in must_off:
            continue
        if units_info[i]['u_i_0'] == 1:
            group1.append(i)
        else:
            group2.append(i)

    committed_indices = must_on + group1

    def compute_bounds(indices):
        total_min = 0.0
        total_max = 0.0
        for i in indices:
            unit = units_info[i]
            if unit['u_i_0'] == 1:
                low = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                high = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                low = unit['p_min_i']
                high = min(unit['p_max_i'], unit['p_start_i'])
            total_min += low
            total_max += high
        return total_min, total_max

    total_min, total_max = compute_bounds(committed_indices)

    if total_min > load_current:
        decommit_candidates = [i for i in group1 if i not in must_on]
        candidate_list = []
        for i in decommit_candidates:
            unit = units_info[i]
            if unit['u_i_0'] == 1:
                low_i = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            else:
                low_i = unit['p_min_i']
            inc_cost = unit['b_i'] + 2 * unit['c_i'] * low_i
            candidate_list.append((inc_cost, i))
        candidate_list.sort(key=lambda x: x[0], reverse=True)

        for inc, i in candidate_list:
            unit = units_info[i]
            if unit['u_i_0'] == 1:
                low_i = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                high_i = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                low_i = unit['p_min_i']
                high_i = min(unit['p_max_i'], unit['p_start_i'])
            new_total_min = total_min - low_i
            new_total_max = total_max - high_i
            if new_total_min <= load_current and new_total_max >= load_current:
                committed_indices.remove(i)
                total_min = new_total_min
                total_max = new_total_max
            if total_min <= load_current:
                break

    if total_max < load_current:
        candidate_list = []
        for i in group2:
            unit = units_info[i]
            p_min = unit['p_min_i']
            s_i = unit['s_i']
            a_i = unit['a_i']
            b_i = unit['b_i']
            c_i = unit['c_i']
            cost_at_min = a_i + b_i * p_min + c_i * p_min**2
            cost_per_mw = (s_i + cost_at_min) / p_min
            candidate_list.append((cost_per_mw, i))
        candidate_list.sort(key=lambda x: x[0])

        for cost_per_mw, i in candidate_list:
            unit = units_info[i]
            p_min = unit['p_min_i']
            effective_max = min(unit['p_max_i'], unit['p_start_i'])
            new_total_min = total_min + p_min
            new_total_max = total_max + effective_max
            committed_indices.append(i)
            total_min = new_total_min
            total_max = new_total_max
            if total_max >= load_current:
                break

    low_bounds = {}
    high_bounds = {}
    for i in committed_indices:
        unit = units_info[i]
        if unit['u_i_0'] == 1:
            low_b = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            high_b = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            low_b = unit['p_min_i']
            high_b = min(unit['p_max_i'], unit['p_start_i'])
        low_bounds[i] = low_b
        high_bounds[i] = high_b

    p_vals = [0.0] * n_units
    for i in committed_indices:
        p_vals[i] = low_bounds[i]

    total_p = sum(p_vals[i] for i in committed_indices)
    remaining = load_current - total_p

    while remaining > 0:
        min_inc = float('inf')
        candidate = None
        for i in committed_indices:
            if p_vals[i] < high_bounds[i]:
                inc = units_info[i]['b_i'] + 2 * units_info[i]['c_i'] * p_vals[i]
                if inc < min_inc:
                    min_inc = inc
                    candidate = i
        if candidate is None:
            break
        available = high_bounds[candidate] - p_vals[candidate]
        if available > remaining:
            p_vals[candidate] += remaining
            remaining = 0
        else:
            p_vals[candidate] += available
            remaining -= available

    u_schedule = [0] * n_units
    p_schedule = [0.0] * n_units
    for i in range(n_units):
        if i in committed_indices:
            u_schedule[i] = 1
            p_schedule[i] = p_vals[i]
        else:
            u_schedule[i] = 0
            p_schedule[i] = 0.0

    schedules = np.array([u_schedule, p_schedule])
    return schedules
