            ub_i = min(unit['p_max_i'], unit['p_start_i'])
        lb.append(lb_i)
        ub.append(ub_i)
        indices.append(unit['index'])

    total_lb = sum(lb)
    total_ub = sum(ub)

    if total_lb > load or total_ub < load:
        return None, None

    units_dispatch = []
    for i, unit in enumerate(online_units):
        mc = unit['b_i'] + 2 * unit['c_i'] * lb[i]
        units_dispatch.append((mc, lb[i], ub[i], i, unit['index']))

    units_dispatch.sort(key=lambda x: x[0])

    p = lb.copy()
    total = total_lb
    gap = load - total

    for i in range(len(units_dispatch)):
