            for cost_val, i in cost_per_mw:
                if total_min > current_load:
                    u[i] = 0
                    total_min -= min_out[i]
                    total_max -= max_out[i]
                    min_out[i] = 0
                    max_out[i] = 0
                else:
                    break

    if total_max < current_load:
        free_off_not_committed = [i for i in free_off if u[i] == 0]
        if free_off_not_committed:
            cost_per_mw = []
            for i in free_off_not_committed:
                unit = units_info[i]
                min_p = unit['p_min_i']
                max_p = min(unit['p_max_i'], unit['p_start_i'])
                total_cost = unit['s_i'] + unit['a_i'] + unit['b_i'] * min_p + unit['c_i'] * min_p**2
                cost_val = total_cost / min_p
                cost_per_mw.append((cost_val, i, min_p, max_p))
            cost_per_mw.sort(key=lambda x: x[0])
            for cost_val, i, min_p, max_p in cost_per_mw:
                if total_max < current_load:
                    u[i] = 1
                    min_out[i] = min_p
                    max_out[i] = max_p
                    total_min += min_p
                    total_max += max_p
                else:
                    break

    free_on_committed = [i for i in free_on if u[i] == 1]
    candidate_to_decommit = [i for i in free_on_committed if units_info[i]['p_i_0'] <= units_info[i]['p_down_i']]
    if candidate_to_decommit:
        cost_per_mw = []
        for i in candidate_to_decommit:
            unit = units_info[i]
            p_val = min_out[i]
            cost_val = (unit['a_i'] + unit['b_i'] * p_val + unit['c_i'] * p_val**2) / p_val
            cost_per_mw.append((cost_val, i))
        cost_per_mw.sort(key=lambda x: x[0], reverse=True)
        for cost_val, i in cost_per_mw:
            if total_max - max_out[i] >= current_load:
                u[i] = 0
                total_min -= min_out[i]
                total_max -= max_out[i]
                min_out[i] = 0
                max_out[i] = 0

    p = [0] * n_units
    for i in range(n_units):
        if u[i] == 1:
            p[i] = min_out[i]

    remaining = current_load - sum(p)
    if remaining > 0:
        candidate_indices = [i for i in range(n_units) if u[i] == 1 and p[i] < max_out[i]]
        while remaining > 0 and candidate_indices:
            min_inc = float('inf')
            best_i = None
            for i in candidate_indices:
                inc = 2 * units_info[i]['c_i'] * p[i] + units_info[i]['b_i']
                if inc < min_inc:
                    min_inc = inc
                    best_i = i
            if best_i is None:
                break
            delta = min(remaining, max_out[best_i] - p[best_i])
            p[best_i] += delta
            remaining -= delta
            if p[best_i] >= max_out[best_i] - 1e-5:
                candidate_indices.remove(best_i)

    schedules = np.zeros((2, n_units))
    schedules[0] = u
    schedules[1] = p
    return schedules

import numpy as np

def economic_dispatch(b_list, c_list, bounds, load):
    total_min = sum(bound[0] for bound in bounds)
    total_max = sum(bound[1] for bound in bounds)
    n_units = len(b_list)
    if total_min >= load:
        return [bound[0] for bound in bounds]
    if total_max <= load:
        return [bound[1] for bound in bounds]

    inc_min = []
    inc_max = []
    for i in range(n_units):
        if c_list[i] > 0:
            inc_min.append(b_list[i] + 2 * c_list[i] * bounds[i][0])
            inc_max.append(b_list[i] + 2 * c_list[i] * bounds[i][1])
        else:
            inc_min.append(b_list[i])
            inc_max.append(b_list[i])

    lambda_low = min(inc_min)
    lambda_high = max(inc_max)
    tol = 0.1
    max_iter = 100
    p_out = [0] * n_units

    for _ in range(max_iter):
        lm = (lambda_low + lambda_high) / 2.0
        total_p = 0.0
        for i in range(n_units):
            if c_list[i] > 0:
                p_i = (lm - b_list[i]) / (2 * c_list[i])
                if p_i < bounds[i][0]:
                    p_i = bounds[i][0]
                elif p_i > bounds[i][1]:
                    p_i = bounds[i][1]
            else:
                if lm > b_list[i]:
                    p_i = bounds[i][1]
                elif lm < b_list[i]:
                    p_i = bounds[i][0]
                else:
                    p_i = bounds[i][0]
            p_out[i] = p_i
            total_p += p_i

        if abs(total_p - load) < tol:
            break
        if total_p < load:
            lambda_low = lm
        else:
            lambda_high = lm

    return p_out

def hybrid_rolling_commitment_optimized(units_info, load):
    n_units = len(units_info)
    current_load = load[0]
    must_run_indices = []
    min_outputs = [0] * n_units
    max_outputs = [0] * n_units

    for i, unit in enumerate(units_info):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_run_indices.append(i)
                min_outputs[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                max_outputs[i] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])

    total_min_A = sum(min_outputs[i] for i in must_run_indices)
    total_max_A = sum(max_outputs[i] for i in must_run_indices)

    u = [0] * n_units
    p = [0] * n_units

    if total_min_A > current_load:
        for i in must_run_indices:
            u[i] = 1
            p[i] = min_outputs[i]
        for i in range(n_units):
            if i not in must_run_indices:
                if units_info[i]['u_i_0'] == 1:
                    if units_info[i]['p_i_0'] <= units_info[i]['p_down_i'] and units_info[i]['p_i_0'] <= units_info[i]['p_shut_i']:
                        u[i] = 0
                    else:
                        u[i] = 1
                        p[i] = max(units_info[i]['p_min_i'], units_info[i]['p_i_0'] - units_info[i]['p_down_i'])
                else:
                    u[i] = 0

    elif current_load <= total_max_A:
        for i in must_run_indices:
            u[i] = 1
        for i in range(n_units):
            if i not in must_run_indices:
                if units_info[i]['u_i_0'] == 1:
                    if units_info[i]['p_i_0'] <= units_info[i]['p_down_i'] and units_info[i]['p_i_0'] <= units_info[i]['p_shut_i']:
                        u[i] = 0
                    else:
                        u[i] = 1
                        p[i] = max(units_info[i]['p_min_i'], units_info[i]['p_i_0'] - units_info[i]['p_down_i'])
                else:
                    u[i] = 0

        committed_units = [i for i in range(n_units) if u[i] == 1]
        bounds_ed = []
        b_list = []
        c_list = []
        for i in committed_units:
            if i in must_run_indices or units_info[i]['u_i_0'] == 1:
                low = max(units_info[i]['p_min_i'], units_info[i]['p_i_0'] - units_info[i]['p_down_i'])
                high = min(units_info[i]['p_max_i'], units_info[i]['p_i_0'] + units_info[i]['p_up_i'])
            else:
                low = units_info[i]['p_min_i']
                high = min(units_info[i]['p_max_i'], units_info[i]['p_start_i'])
            bounds_ed.append((low, high))
            b_list.append(units_info[i]['b_i'])
            c_list.append(units_info[i]['c_i'])

        p_committed = economic_dispatch(b_list, c_list, bounds_ed, current_load)
        for idx, val in zip(committed_units, p_committed):
            p[idx] = val

    else:
        candidate_indices = []
        for i in range(n_units):
            if i in must_run_indices:
                continue
            if units_info[i]['u_i_0'] == 1:
                candidate_indices.append(i)
            else:
                if units_info[i]['t_i_0'] < 0 and -units_info[i]['t_i_0'] < units_info[i]['t_off_min_i']:
                    continue
                candidate_indices.append(i)

        candidate_costs = []
        for i in candidate_indices:
            unit = units_info[i]
            if unit['u_i_0'] == 1:
                total_cost = unit['a_i'] + unit['b_i'] * unit['p_min_i'] + unit['c_i'] * (unit['p_min_i'] ** 2)
            else:
                total_cost = unit['s_i'] + unit['a_i'] + unit['b_i'] * unit['p_min_i'] + unit['c_i'] * (unit['p_min_i'] ** 2)
            cost_per_mw = total_cost / unit['p_min_i']
            candidate_costs.append((cost_per_mw, i))

        candidate_costs.sort(key=lambda x: x[0])
        sorted_candidates = [idx for _, idx in candidate_costs]
        total_min = total_min_A
        total_max = total_max_A
        committed_candidates = []

        for idx in sorted_candidates:
            if units_info[idx]['u_i_0'] == 1:
                low = max(units_info[idx]['p_min_i'], units_info[idx]['p_i_0'] - units_info[idx]['p_down_i'])
                high = min(units_info[idx]['p_max_i'], units_info[idx]['p_i_0'] + units_info[idx]['p_up_i'])
            else:
                low = units_info[idx]['p_min_i']
                high = min(units_info[idx]['p_max_i'], units_info[idx]['p_start_i'])
            total_min += low
            total_max += high
            committed_candidates.append(idx)
            if total_max >= current_load:
                break

        for i in must_run_indices:
            u[i] = 1
        for i in committed_candidates:
            u[i] = 1
        for i in range(n_units):
            if u[i] == 0 and units_info[i]['u_i_0'] == 1:
                if units_info[i]['p_i_0'] <= units_info[i]['p_down_i'] and units_info[i]['p_i_0'] <= units_info[i]['p_shut_i']:
                    u[i] = 0
                else:
                    u[i] = 1
                    p[i] = max(units_info[i]['p_min_i'], units_info[i]['p_i_0'] - units_info[i]['p_down_i'])
                    if i not in committed_candidates:
                        committed_candidates.append(i)

        committed_units = must_run_indices + committed_candidates
        bounds_ed = []
        b_list = []
        c_list = []
        for i in committed_units:
            if i in must_run_indices or units_info[i]['u_i_0'] == 1:
                low = max(units_info[i]['p_min_i'], units_info[i]['p_i_0'] - units_info[i]['p_down_i'])
                high = min(units_info[i]['p_max_i'], units_info[i]['p_i_0'] + units_info[i]['p_up_i'])
            else:
                low = units_info[i]['p_min_i']
                high = min(units_info[i]['p_max_i'], units_info[i]['p_start_i'])
            bounds_ed.append((low, high))
            b_list.append(units_info[i]['b_i'])
            c_list.append(units_info[i]['c_i'])

        p_committed = economic_dispatch(b_list, c_list, bounds_ed, current_load)
        for idx, val in zip(committed_units, p_committed):
            p[idx] = val

    schedules = np.array([u, p])
    return schedules

import numpy as np

def economic_dispatch(units_info, committed_indices, p_min_feasible, p_max_feasible, load):
    n_committed = len(committed_indices)
    if n_committed == 0:
        return np.zeros(0)

    b = []
    c = []
    p_min = []
    p_max = []
    for idx in committed_indices:
        unit = units_info[idx]
        b.append(unit['b_i'])
        c.append(unit['c_i'])
        p_min.append(p_min_feasible[idx])
        p_max.append(p_max_feasible[idx])

    total_min = sum(p_min)
    total_max = sum(p_max)
    if total_min > load:
        return p_min
    if total_max < load:
        return p_max

    low_lambda = min(2 * c_i * p_min_i + b_i for b_i, c_i, p_min_i in zip(b, c, p_min))
    high_lambda = max(2 * c_i * p_max_i + b_i for b_i, c_i, p_max_i in zip(b, c, p_max))

    tol = 1e-5
    max_iter = 1000
    iter_count = 0
    p_vals = [0.0] * n_committed

    while (high_lambda - low_lambda) > tol and iter_count < max_iter:
        mid_lambda = (low_lambda + high_lambda) / 2.0
        total_p = 0.0
        for i in range(n_committed):
            if c[i] == 0:
                if mid_lambda < b[i]:
                    p_vals[i] = p_min[i]
                elif mid_lambda > b[i]:
                    p_vals[i] = p_max[i]
                else:
                    p_vals[i] = p_min[i]
            else:
                p_uncon = (mid_lambda - b[i]) / (2 * c[i])
                p_vals[i] = max(p_min[i], min(p_max[i], p_uncon))
            total_p += p_vals[i]

        if total_p < load:
            low_lambda = mid_lambda
        else:
            high_lambda = mid_lambda
        iter_count += 1

    total_p = sum(p_vals)
    if abs(total_p - load) > tol:
        if total_p < load:
            deficit = load - total_p
            indices_not_max = [i for i in range(n_committed) if p_vals[i] < p_max[i]]
            inc_cost = []
            for i in indices_not_max:
                if c[i] == 0:
                    cost_val = b[i]
                else:
                    cost_val = 2 * c[i] * p_vals[i] + b[i]
                inc_cost.append((i, cost_val))
            inc_cost.sort(key=lambda x: x[1])
            for (i, _) in inc_cost:
                if deficit <= 0:
                    break
                headroom = p_max[i] - p_vals[i]
                if headroom > 0:
                    add = min(deficit, headroom)
                    p_vals[i] += add
                    deficit -= add
        else:
            surplus = total_p - load
            indices_not_min = [i for i in range(n_committed) if p_vals[i] > p_min[i]]
            inc_cost = []
            for i in indices_not_min:
                if c[i] == 0:
                    cost_val = b[i]
                else:
                    cost_val = 2 * c[i] * p_vals[i] + b[i]
                inc_cost.append((i, cost_val))
            inc_cost.sort(key=lambda x: x[1], reverse=True)
            for (i, _) in inc_cost:
                if surplus <= 0:
                    break
                reduction_room = p_vals[i] - p_min[i]
                if reduction_room > 0:
                    reduction = min(surplus, reduction_room)
                    p_vals[i] -= reduction
                    surplus -= reduction
    return p_vals

def adaptive_rolling_commitment_v1(units_info, load):
    current_load = load[0]
    n_units = len(units_info)
    u_out = np.zeros(n_units, dtype=int)
    p_out = np.zeros(n_units)

    must_run = [False] * n_units
    p_min_feasible = [0.0] * n_units
    p_max_feasible = [0.0] * n_units

    for i, unit in enumerate(units_info):
        u_i0 = unit['u_i_0']
        t_i0 = unit['t_i_0']
        p_i0 = unit['p_i_0']
        p_min_i = unit['p_min_i']
        p_max_i = unit['p_max_i']
        p_down_i = unit['p_down_i']
        p_shut_i = unit['p_shut_i']
        t_on_min_i = unit['t_on_min_i']

        if u_i0 == 1:
            if t_i0 < t_on_min_i or p_i0 > p_shut_i or p_i0 > p_down_i:
                must_run[i] = True
            p_min_feasible[i] = max(p_min_i, p_i0 - p_down_i)
            p_max_feasible[i] = min(p_max_i, p_i0 + unit['p_up_i'])
        else:
            p_min_feasible[i] = p_min_i
            p_max_feasible[i] = min(p_max_i, unit['p_start_i'])

    total_min_must = 0.0
    total_max_must = 0.0
    for i in range(n_units):
        if must_run[i]:
            total_min_must += p_min_feasible[i]
            total_max_must += p_max_feasible[i]

    if total_min_must > current_load:
        for i in range(n_units):
            if must_run[i]:
                u_out[i] = 1
                p_out[i] = p_min_feasible[i]
            else:
                u_out[i] = 0
                p_out[i] = 0.0
        return np.array([u_out, p_out])

    elif current_load <= total_max_must:
        committed_indices = [i for i in range(n_units) if must_run[i]]
        if committed_indices:
            p_committed = economic_dispatch(units_info, committed_indices, p_min_feasible, p_max_feasible, current_load)
            for idx, i in enumerate(committed_indices):
                u_out[i] = 1
                p_out[i] = p_committed[idx]
        return np.array([u_out, p_out])

    else:
        candidate_indices = []
        for i in range(n_units):
            if must_run[i]:
                continue
            unit = units_info[i]
            if unit['u_i_0'] == 1:
                candidate_indices.append(i)
            else:
                if unit['t_i_0'] <= -unit['t_off_min_i']:
                    candidate_indices.append(i)

        candidate_costs = []
        for i in candidate_indices:
            unit = units_info[i]
            a_i = unit['a_i']
            b_i_val = unit['b_i']
            c_i_val = unit['c_i']
            p_min_i = unit['p_min_i']
            startup_cost = unit['s_i'] if unit['u_i_0'] == 0 else 0.0
            total_cost = a_i + b_i_val * p_min_i + c_i_val * (p_min_i**2) + startup_cost
            cost_per_mw = total_cost / p_min_i
            candidate_costs.append((i, cost_per_mw))

        candidate_costs.sort(key=lambda x: x[1])
        sorted_candidate_indices = [idx for idx, _ in candidate_costs]

        committed_indices = [i for i in range(n_units) if must_run[i]]
        total_committed_max = total_max_must

        for i in sorted_candidate_indices:
            if total_committed_max >= current_load:
                break
            committed_indices.append(i)
            total_committed_max += p_max_feasible[i]

        if committed_indices:
            p_committed = economic_dispatch(units_info, committed_indices, p_min_feasible, p_max_feasible, current_load)
            for idx, i in enumerate(committed_indices):
                u_out[i] = 1
                p_out[i] = p_committed[idx]

        return np.array([u_out, p_out])
