def economic_dispatch(online_units, load):
    n = len(online_units)
    lb = []
    ub = []
    indices = []
    for unit in online_units:
        if unit['u_i_0'] == 1:
            lb_i = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
            ub_i = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
        else:
            lb_i = unit['p_min_i']
            ub_i = min(unit['p_max_i'], unit['p_start_i'])
        lb.append(lb_i)
        ub.append(ub_i)
        indices.append(unit['index'])

    total_lb = sum(lb)
    total_ub = sum(ub)

    if total_lb > load or total_ub < load:
        return None, None

    units_dispatch = []
    for i, unit in enumerate(online_units):
        mc = unit['b_i'] + 2 * unit['c_i'] * lb[i]
        units_dispatch.append((mc, lb[i], ub[i], i, unit['index']))

    units_dispatch.sort(key=lambda x: x[0])

    p = lb.copy()
    total = total_lb
    gap = load - total

    for i in range(len(units_dispatch)):
        mc, lb_i, ub_i, idx, unit_idx = units_dispatch[i]
        add = min(gap, ub_i - lb_i)
        p[idx] += add
        gap -= add
        if gap <= 1e-5:
            break

    if gap > 1e-5:
        return None, None

    dispatch_dict = {}
    for i in range(len(online_units)):
        dispatch_dict[indices[i]] = p[i]

    total_cost = 0
    for i, unit in enumerate(online_units):
        if unit['u_i_0'] == 0:
            total_cost += unit['s_i']
        total_cost += unit['a_i'] + unit['b_i'] * p[i] + unit['c_i'] * (p[i] ** 2)

    return dispatch_dict, total_cost

def commit_units_by_min_startup_cost_heuristic(units_info, load):
    current_load = load[0]
    n_units = len(units_info)
    must_on = []
    must_off = []
    free_on = []
    free_off = []

    for i, unit in enumerate(units_info):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_on.append(i)
            else:
                free_on.append(i)
        else:
            if unit['t_i_0'] < 0 and abs(unit['t_i_0']) < unit['t_off_min_i']:
                must_off.append(i)
            else:
                free_off.append(i)

    online_set = must_on + free_on
    online_units = [{'index': i, **units_info[i]} for i in online_set]
    dispatch_result, _ = economic_dispatch(online_units, current_load)

    if dispatch_result is None:
        candidates = []
        for i in free_off:
            unit = units_info[i]
            key = unit['s_i'] / unit['p_max_i'] + (unit['b_i'] + 2 * unit['c_i'] * unit['p_min_i'])
            candidates.append((key, i))
        candidates.sort(key=lambda x: x[0])

        for key, i in candidates:
            online_set.append(i)
            online_units = [{'index': j, **units_info[j]} for j in online_set]
            dispatch_result, _ = economic_dispatch(online_units, current_load)
            if dispatch_result is not None:
                break

        if dispatch_result is None:
            dispatch_result = {}
            for i in online_set:
                if units_info[i]['u_i_0'] == 1:
                    ub_i = min(units_info[i]['p_max_i'], units_info[i]['p_i_0'] + units_info[i]['p_up_i'])
                else:
                    ub_i = min(units_info[i]['p_max_i'], units_info[i]['p_start_i'])
                dispatch_result[i] = ub_i

    else:
        candidates = []
        for i in free_on:
            if i in online_set and i in dispatch_result:
                if units_info[i]['u_i_0'] == 1:
                    lb_i = max(units_info[i]['p_min_i'], units_info[i]['p_i_0'] - units_info[i]['p_down_i'])
                else:
                    lb_i = units_info[i]['p_min_i']
                if abs(dispatch_result[i] - lb_i) < 1e-5:
                    saving = units_info[i]['a_i'] + units_info[i]['b_i'] * dispatch_result[i] + units_info[i]['c_i'] * (dispatch_result[i] ** 2)
                    candidates.append((saving, i))

        candidates.sort(key=lambda x: x[0], reverse=True)
        for saving, i in candidates:
            new_online_set = [j for j in online_set if j != i]
            new_online_units = [{'index': j, **units_info[j]} for j in new_online_set]
            new_dispatch, _ = economic_dispatch(new_online_units, current_load)
            if new_dispatch is not None:
                online_set = new_online_set
                dispatch_result = new_dispatch
                break

    schedules = np.zeros((2, n_units))
    for i in range(n_units):
        if i in online_set and i in dispatch_result:
            schedules[0, i] = 1
            schedules[1, i] = dispatch_result[i]
        else:
            schedules[0, i] = 0
            schedules[1, i] = 0

    return schedules
