import numpy as np

def refined_rolling_commitment_v5(units_info, load):
    n_units = len(units_info)
    must_on = []
    must_off = []
    free_on = []
    free_off = []

    for i, unit in enumerate(units_info):
        if unit['u_i_0'] == 1:
            if unit['t_i_0'] < unit['t_on_min_i'] or unit['p_i_0'] > unit['p_shut_i']:
                must_on.append(i)
            else:
                free_on.append(i)
        else:
            if unit['t_i_0'] > -unit['t_off_min_i']:
                must_off.append(i)
            else:
                free_off.append(i)

    u = [0] * n_units
    min_out = [0] * n_units
    max_out = [0] * n_units

    for i in must_on:
        u[i] = 1
    for i in free_on:
        u[i] = 1
    for i in must_off:
        u[i] = 0
    for i in free_off:
        u[i] = 0

    for i, unit in enumerate(units_info):
        if u[i] == 1:
            if unit['u_i_0'] == 1:
                min_out[i] = max(unit['p_min_i'], unit['p_i_0'] - unit['p_down_i'])
                max_out[i] = min(unit['p_max_i'], unit['p_i_0'] + unit['p_up_i'])
            else:
                min_out[i] = unit['p_min_i']
                max_out[i] = min(unit['p_max_i'], unit['p_start_i'])
        else:
            min_out[i] = 0
            max_out[i] = 0

    total_min = sum(min_out)
    total_max = sum(max_out)
    current_load = load[0]

    if total_min > current_load:
        candidate_decommit = [i for i in free_on if u[i] == 1]
        cost_per_mw = []
        for i in candidate_decommit:
            unit = units_info[i]
            p_val = min_out[i]
            cost_val = (unit['a_i'] + unit['b_i'] * p_val + unit['c_i'] * p_val**2) / p_val
            cost_per_mw.append((cost_val, i))
        cost_per_mw.sort(key=lambda x: x[0], reverse=True)
        for cost_val, i in cost_per_mw:
            if total_min > current_load and (total_max - max_out[i] >= current_load):
                u[i] = 0
                total_min -= min_out[i]
                total_max -= max_out[i]
                min_out[i] = 0
                max_out[i] = 0

    if total_max < current_load:
        candidate_commit = [i for i in free_off if u[i] == 0]
        cost_per_mw = []
        for i in candidate_commit:
            unit = units_info[i]
            min_p = unit['p_min_i']
            max_p = min(unit['p_max_i'], unit['p_start_i'])
            total_cost = unit['s_i'] + unit['a_i'] + unit['b_i'] * min_p + unit['c_i'] * min_p**2
            cost_val = total_cost / min_p
            cost_per_mw.append((cost_val, i, min_p, max_p))
        cost_per_mw.sort(key=lambda x: x[0])
        for cost_val, i, min_p, max_p in cost_per_mw:
            if total_max < current_load:
                u[i] = 1
                min_out[i] = min_p
                max_out[i] = max_p
                total_min += min_p
                total_max += max_p
            else:
                break

    if total_min > current_load:
        candidate_decommit = []
        for i in range(n_units):
            if u[i] == 1 and i not in must_on and (total_max - max_out[i] >= current_load):
                candidate_decommit.append(i)
        cost_per_mw = []
        for i in candidate_decommit:
            unit = units_info[i]
            p_val = min_out[i]
            cost_val = (unit['a_i'] + unit['b_i'] * p_val + unit['c_i'] * p_val**2) / p_val
            cost_per_mw.append((cost_val, i))
        cost_per_mw.sort(key=lambda x: x[0], reverse=True)
        for cost_val, i in cost_per_mw:
            if total_min > current_load:
                u[i] = 0
                total_min -= min_out[i]
                total_max -= max_out[i]
                min_out[i] = 0
                max_out[i] = 0
            else:
                break

    candidate_decommit = []
    for i in range(n_units):
        if u[i] == 1 and i not in must_on and (total_max - max_out[i] >= current_load):
            candidate_decommit.append(i)
    cost_per_mw = []
    for i in candidate_decommit:
        unit = units_info[i]
        p_val = min_out[i]
        cost_val = (unit['a_i'] + unit['b_i'] * p_val + unit['c_i'] * p_val**2) / p_val
        cost_per_mw.append((cost_val, i))
    cost_per_mw.sort(key=lambda x: x[0], reverse=True)
    for cost_val, i in cost_per_mw:
        if total_max - max_out[i] >= current_load:
            u[i] = 0
            total_min -= min_out[i]
            total_max -= max_out[i]
            min_out[i] = 0
            max_out[i] = 0

    p = [0] * n_units
    for i in range(n_units):
        if u[i] == 1:
            p[i] = min_out[i]

    remaining = current_load - sum(p)
    if remaining > 1e-5:
        candidate_indices = [i for i in range(n_units) if u[i] == 1 and p[i] < max_out[i] - 1e-5]
        while remaining > 1e-5 and candidate_indices:
            min_inc = float('inf')
            best_i = None
            for i in candidate_indices:
                inc = 2 * units_info[i]['c_i'] * p[i] + units_info[i]['b_i']
                if inc < min_inc:
                    min_inc = inc
                    best_i = i
            if best_i is None:
                break
            delta = min(remaining, max_out[best_i] - p[best_i])
            p[best_i] += delta
            remaining -= delta
            if p[best_i] >= max_out[best_i] - 1e-5:
                candidate_indices.remove(best_i)

    schedules = np.zeros((2, n_units))
    schedules[0] = u
    schedules[1] = p
    return schedules
